	public ThrowingShade()
	{
		Name = "Throwing Shade";
		QuestId.Add(47203);
		Step.AddRange(new[] { 1, 1, 1, 1, 1, 1 });
	}
	void Log(string text)
	{
		Logging.WriteDebug("[" + Name + "] " + text);
	}
	bool InVoid
	{
		get
		{
			return ObjectManager.Me.HaveBuff(242960);
		}
	}
	public override bool Pulse()
	{
		var p = NpcTurnIn.Position;
		var npcID = NpcTurnIn.Id;
		if (!InVoid)
		{
			Conditions.ForceIgnoreIsAttacked = false;
			Questing.Mount = true;
			Log("goto void");
			p = Positions[0];
			npcID = Npcs[0];
			if (GoToTask.ToPosition(p, 10, false, MoveNotInVoid))
			{
				GoToTask.ToPositionAndIntecractWithNpc(p, npcID, 1);
			}
			return true;
		}
		Conditions.ForceIgnoreIsAttacked = true;
		Questing.Mount = false;
		if (!Questing.ObjectiveComplete(this, 3))
		{
			Log("goto Dismantled Portal");
			p = Positions[1];
			npcID = Npcs[1];
		}
		else if (!Questing.ObjectiveComplete(this, 4))
		{
			Log("goto Image of Saprish");
			p = Positions[2];
			npcID = Npcs[2];
		}
		else if (!Questing.ObjectiveComplete(this, 5))
		{
			Log("goto Image of Voidforge");
			p = Positions[3];
			npcID = Npcs[3];
		}
		else
		{
			Log("goto " + NpcTurnIn.Name);
		}
		if (ObjectManager.Me.Position.DistanceTo(p) < 12)
		{
			if (GoToTask.ToPosition(p, 5, false, MoveInVoid))
			{
				GoToTask.ToPositionAndIntecractWithNpc(p, npcID, 1);
			}
			return true;
		}
		var path = Questing.PathClampDirected(Path, ObjectManager.Me.Position, p);
		Questing.GotoPathFromNear(path, true, MoveInVoid);
		return true;
	}
	bool MoveInVoid(object c)
	{
		if (InVoid)
			return true;

		Log("goto failed. fall from void");
		return false;
	}
	bool MoveInVoid()
	{
		return MoveInVoid(null);
	}
	bool MoveNotInVoid(object c)
	{
		if (!InVoid)
			return true;
		Log("goto failed. fall in void");
		return false;
	}
	public override bool IsComplete()
	{
		return !ObjectManager.Me.HaveBuff(242960) && Questing.ObjectiveComplete(this, 6);
	}
	public override bool PickUp()
	{
		Questing.CloseNPCFrame();
		return base.PickUp();
	}
	public override bool TurnIn()
	{
		Questing.CloseNPCFrame();
		return base.TurnIn();
	}
}

var hearth = Questing.FindObject(273833);
