using wManager.Wow.Enums;
#endif

public class ABeaconintheDark : QuestClass
{
	public ABeaconintheDark()
	{
		Name = "A Beacon in the Dark";
		QuestId.Add(47220);
		Step.AddRange(new[] { 0 });
	}
	public override bool PickUp()
	{
		if (!ArgusHelper.InMacAreeOpen)
		{
			ArgusHelper.ToMacAreeOpen();
			return true;
		}
		return base.PickUp();
	}
	public override bool TurnIn()
	{
		if (!ArgusHelper.InVendicarMacAree)
		{
			ArgusHelper.ToVendicarMacAree();
			return true;
		}
		return base.TurnIn();
	}
}
