  if (ObjectManager.Me.IsHorde)
   removeLabel = "(A)";

  foreach (var npc in npcs)
  {
   if (npc.Name.Contains(removeLabel))
   {
    Logging.WriteDebug("Clear npc=" + npc.Name + " from list. coz opposite faction");
    npc.PickUpQuests.Clear();
    npc.TurnInQuests.Clear();
   }
  }

