#if VISUAL_STUDIO
using robotManager.Helpful;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading;
using System.Threading.Tasks;
using wManager.Wow.Bot.Tasks;
using wManager.Wow.Class;
using wManager.Wow.Helpers;
using wManager.Wow.ObjectManager;
using wManager.Wow.Enums;
#endif

public class YoureaMeanOne : QuestClass
{
	public YoureaMeanOne()
	{
		Name = "You're a Mean One...";
		QuestId.Add(6983);
		QuestId.Add(7043);
		Step.AddRange(new[] { 1, 1 });
	}
	static void Log(string text)
	{
		Logging.WriteDebug("[You're a Mean One...] " + text);
	}
	public override bool PickUp()
	{
		//if (Traveler.InEasternKingdoms)
		if (Usefuls.AreaId == 267) //hillbard foothills
		{
			return base.PickUp();
		}
		if (Traveler.InDalaraPortraitRoom)
		{
			Traveler.Portals.BROKENISLES_DALARANCRATER.Use();
			return true;
		}
		Traveler.ToDalaranPortraitRoom();
		return true;
	}
	public override bool Pulse()
	{
		if (Usefuls.AreaId != 267)
		{
			if (Traveler.InDalaraPortraitRoom)
			{
				Traveler.Portals.BROKENISLES_DALARANCRATER.Use();
				return true;
			}
			Traveler.ToDalaranPortraitRoom();
			return true;
		}
		var p = new Vector3(220.6472, -279.3594, 146.634, "None");
		if (!Questing.ObjectiveComplete(this, 1))
		{
			var goodsID = 209506;
			if (ObjectManager.Me.IsAlliance)
				goodsID = 209497;
			var goods = Questing.FindObject(goodsID);
			if (goods != null && goods.IsValid)
			{
				Log("get goods " + goods.Name);
				GoToTask.ToPositionAndIntecractWithGameObject(goods.Position, goods.Entry, 1);
				return true;
			}
		}
		if (!Questing.ObjectiveComplete(this, 2))
		{
			if (Questing.Attack(54499))
				return true;

			var metzen = Questing.FindUnit(15664);
			if (metzen != null && metzen.IsValid)
			{
				Log("talk deer " + metzen.Name);
				GoToTask.ToPositionAndIntecractWithNpc(metzen.Position, metzen.Entry, 1);
				return true;
			}
		}
		GoToTask.ToPosition(p);
		return true;
	}
	public override bool TurnIn()
	{
		if (ObjectManager.Me.IsHorde && !Traveler.InCapitalOpen)
		{
			Traveler.ToCapitalOpen();
			return true;
		}
		if (ObjectManager.Me.IsAlliance && !Traveler.InIronforge)
		{
			Traveler.ToIronforge();
			return true;
		}
		return base.TurnIn();
	}
}

#if VISUAL_STUDIO
using robotManager.Helpful;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading;
using System.Threading.Tasks;
using wManager.Wow.Bot.Tasks;
