using wManager.Wow.ObjectManager;
using wManager.Wow.Enums;
#endif

public class ToWinterVeilQuestsArea : QuestClass
{
	public static Vector3 Center = new Vector3(7139.887, 4716.024, 225.5821, "None");
	public static int FrostfireRidgeAreaID = 6720;
	public static int FrostfireRidgeMapID = 962;
	static bool _taxiFixed = false;
	static int _taxiTry = 20;
	public ToWinterVeilQuestsArea()
	{
		Name = "To Winter Veil Quests Area";
		QuestId.Add(39651);
		QuestId.Add(39649);
		QuestId.Add(39668);
		QuestId.Add(39648);
		Step.AddRange(new[] { 0 });
	}
	static void Log(string text)
	{
		Logging.WriteDebug("[Winter Veil] " + text);
	}
	public override bool Pulse()
	{
		FixTaxi();
		if (TryTakeTaxi())
			return true;

		Traveler.Goto(Center);
		return true;
	}
	bool TryTakeTaxi()
	{
		if (Usefuls.AreaId == FrostfireRidgeAreaID || _taxiTry < 0)
			return false;

		_taxiTry -= 1;
		Logging.WriteDebug("Try to take taxi to frostfire ridge. Tryes left=" + _taxiTry);
		Traveler.TryTaxi(Usefuls.ContinentId, FrostfireRidgeMapID, Center);
		return true;
	}
	void FixTaxi()
	{
		if (_taxiFixed)
			return;

		Logging.WriteDebug("Fix taxi nodes");
		_taxiFixed = true;
		/*
		lock (Taxi.TaxiList.Locker)
		{
			foreach (var node in Taxi.TaxiList.Nodes)
			{
				Taxi.TaxiList.Active(node);
			}
		}
		//*/
		if (ObjectManager.Me.IsHorde)
		{
			AddTaxiNode("Bron Skyhorn", new Vector3(5575.775, 4563.67, 136.2239, "None"), (int)ContinentId.FWHordeGarrisonLevel2);
		}
		if (ObjectManager.Me.IsAlliance)
		{
			AddTaxiNode("Dungar Longdrink", new Vector3(1864.33, 217.8108, 76.66184, "None"), (int)ContinentId.SMVAllianceGarrisonLevel2);
		}
		AddTaxiNode("Brenna Skymoor", new Vector3(7400.537, 4360.385, 126.406, "None"), (int)ContinentId.Draenor);
	}
	void AddTaxiNode(string name, Vector3 p, int continent)
	{
		lock (Taxi.TaxiList.Locker)
		{
			var t = new TaxiNode(1, name, p, continent);
			if (t.IsValid())
			{
				t.Active = true;
				Taxi.TaxiList.AddOrEditForCurrentPlayer(t);
			}
		}
	}
	public override bool IsComplete()
	{
		var questsComplete = true;
		foreach (var questID in QuestId)
		{
			Questing.ObjectiveComplete(this, 1);
			if (Quest.HasQuest(questID) && !Quest.IsObjectiveComplete(1, questID))
			{
				questsComplete = false;
				break;
			}
		}
		if (questsComplete)
		{
			Log("all quests objectives completed");
			return true;
		}
		return ObjectManager.Me.Position.DistanceTo2D(Center) < 300;
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsCompleted()
	{
		return false;
	}
}
