	public static class Instances
	{
		public static bool InBlackTemple
		{
			get
			{
				return Usefuls.ContinentId == (int) ContinentId.BlackTemple;
			}
		}

		public static void ToBlackTemple()
		{
			if (InBlackTemple)
			{
				TravelLog("Im in Black Temple");
			}
			else if (NearBlackTemple)
			{
				TravelLog("Im near Black Temple, going inside");
				GoToTask.ToPosition(Coords.BLACKTEMPLE_MOVEIN);
			}
			else
			{
				ToBlackTempleNear();
			}
		}

		public static bool NearBlackTemple
		{
			get
			{
				if (InBlackTemple)
					return true;
				if (!InOutlands)
					return false;

				return ObjectManager.Me.Position.DistanceTo2D(Coords.BLACKTEMPLE_NEAR) < 500;
			}
		}

		public static void ToBlackTempleNear()
		{
			uint teleportMedalion = 32757;  //http://www.wowhead.com/item=32757/blessed-medallion-of-karabor
			if (InBlackTemple || NearBlackTemple)
			{
				TravelLog("Im in Black Temple or near");
			}
			else if (ItemsManager.HasItemById(teleportMedalion))
			{
				TravelLog("Equipping teleport item");
				ItemsManager.EquipItemByName(ItemsManager.GetNameById(teleportMedalion));
				Thread.Sleep(35 * 1000);
				TravelLog("Teleporting to Black Temple");
				ItemsManager.UseItem(teleportMedalion);
				Usefuls.WaitIsCasting();
			}
			else if (InOutlands)
			{
				TravelLog("Im in Outlands. Moving near Black Temple");
				GoToTask.ToPosition(Coords.BLACKTEMPLE_NEAR);
			}
			else
			{
				TravelLog("Im in somewhere else. Go to Shattrath");
				ToShattrath();
				Thread.Sleep(10 * 1000);
				ToBlackTempleNear();
			}
		}

		// sunwell
		public static bool InSunwell
		{
			get
			{
				return Usefuls.ContinentId == (int)ContinentId.SunwellPlateau;
			}
		}

		public static void ToSunwell()
		{
			if (InSunwell)
			{
				TravelLog("Im in Sunwell");
			}
			else if (InQuelDanas)
			{
				TravelLog("Im in Quel Danas, goin to sunwell");
				GoToTask.ToPosition(Coords.SUNWELLPLATEAU_MOVEIN);
			}
			else
			{
				ToQuelDanas();
			}
		}

	}

	public enum AreaId
	{
		QuelDanas = 4080,
		Shattrath = 3703,
		DalaranNorthrend = 4395,
		DalaranBrokenIsles = 7502,
		PandariaShrineAlliance = 6553, //alliance PandariaShrineOfSevenStars
		PandariaShrineAllianceNear = 6142,
		PandariaShrineHorde = 6141, //horde PandariaShrineOfTwoMoons
		PandariaShrineHordeNear = 6554,
	}

	public class PortalInfo
	{
		public Vector3 position;
		public int objectId;
		public PortalInfo(Vector3 pos, int id)
		{
			position = pos;
			objectId = id;
		}

		public void Use()
		{
			GoToTask.ToPosition(position);
			MountTask.DismountMount();
			GoToTask.ToPositionAndIntecractWithGameObject(position, objectId);
		}
	}

	static void TravelLog(string message)
	{
		Logging.WriteDebug("[Traveler] " + message);
	}

	static void TravelError(string message)
	{
		Logging.WriteError("[Traveler] " + message);
	}

	public Traveler()
	{
		Name = "Traveler";
		SetDefaultSettings();
	}

	static Traveler()
	{
	}

	public static void SetDefaultSettings()
	{
		var config = wManager.wManagerSetting.CurrentSetting;
		config.CloseIfPlayerTeleported = false;
		config.MaxUnitsNear = 99;
		config.CanAttackUnitsAlreadyInFight = true;
		config.AttackElite = true;
		config.AttackBeforeBeingAttacked = false;
		config.SearchRadius = 85;
		config.SkinNinja = false;
		config.SkinMobs = false;
		config.HarvestHerbs = false;
		config.HarvestMinerals = false;
		config.HarvestTimber = false;
		config.UseFlyingMount = true;
		config.UseGroundMount = true;
		config.UseMount = true;
		config.IgnoreFightGoundMount = true;
		wManager.Wow.Helpers.CVar.SetCVar("autoDismount", "1");
		wManager.Wow.Helpers.CVar.SetCVar("autoDismountFlying", "1");
		wManager.Wow.Helpers.Conditions.ForceIgnoreIsAttacked = false;
		Logging.Write("set default settings");
	}

}

public sealed class Instancer : wManager.Wow.Class.QuestClass
{
	//796 - black temple
	static System.Collections.Generic.List<string> _cache = new System.Collections.Generic.List<string>();
	static System.Collections.Generic.List<MapInfo> _maps = new System.Collections.Generic.List<MapInfo>();
	static robotManager.Helpful.Timer _timer = new robotManager.Helpful.Timer();

	public static bool Found(int mobId, float distance = 150)
	{
		var mobEntry = wManager.Wow.ObjectManager.ObjectManager.GetNearestWoWUnit(wManager.Wow.ObjectManager.ObjectManager.GetWoWUnitByEntry(mobId));
		if (mobEntry != null && mobEntry.IsAlive && mobEntry.IsValid)
		{
			if (mobEntry.Position.DistanceTo2D(wManager.Wow.ObjectManager.ObjectManager.Me.Position) < distance)
			{
				return true;
			}
		}
		return false;
	}

	public static bool Alive(int instanceMapId, int bossNum)
	{
		return !Killed(instanceMapId, bossNum);
	}

	public static bool Killed(int instanceMapId, int bossNum)
	{
		UpdateInstanceKills();
		var mapName = GetMapName(instanceMapId);
		if (string.IsNullOrEmpty(mapName))
			return false;

		var bossName = mapName + bossNum;
		if (_cache.Contains(bossName))
			return true;

		return false;
	}

	static string GetMapName(int mapId)
	{
		foreach (var map in _maps)
		{
			if (map.id == mapId)
				return map.name;
		}
		var mapName = FindMapName(mapId);
		if (string.IsNullOrEmpty(mapName))
			return "";

		_maps.Add( new MapInfo(mapId, mapName) );
		return mapName;
	}

	static string FindMapName(int mapId)
	{
		var luaCode = @"
local mapname = GetMapNameByID({0});
if (not mapname) then
	return '';
end
return mapname;
";
		string toRun = string.Format(luaCode, mapId);
		string mapName = wManager.Wow.Helpers.Lua.LuaDoString<string>(toRun);
		InstancerLog("find map name for mapId=" + mapId + " result=" + mapName);
		return mapName;
	}

	static void UpdateInstanceKills()
	{
		if (!_timer.IsReady)
			return;

		_timer.Reset(30 * 1000);
		_cache = new System.Collections.Generic.List<string>();

		var luaCode = @"
local killList = '';
local n = GetNumSavedInstances()
for k=1,n do
	local name, id, reset, difficulty, locked, extended, instanceIDMostSig, isRaid, maxPlayers, difficultyName, numEncounters, encounterProgress = GetSavedInstanceInfo(k)
    if (locked and encounterProgress > 0) then
		for i = 1, numEncounters do
			local bossName, text, isKilled = GetSavedInstanceEncounterInfo(k, i)
			if (isKilled) then
				killList = killList .. name .. i .. '#LUASEPARATOR#';
			end
		end
	end
end
return killList;
		";

		string toRun = string.Format(luaCode.Replace("#LUASEPARATOR#", wManager.Wow.Helpers.Lua.ListSeparator));
		var killList = wManager.Wow.Helpers.Lua.LuaDoString<System.Collections.Generic.List<string>>(toRun);
		_cache = killList;
		_cache.RemoveAll(str => string.IsNullOrEmpty(str));
		InstancerLog("kill list updated. count=" + _cache.Count);
		/*
		foreach (var kill in _cache)
		{
			InstancerLog(" >> " + kill);
		}
		//*/
	}

	static void InstancerLog(string message)
	{
		robotManager.Helpful.Logging.WriteDebug("[Instancer] " + message);
	}

	static void InstancerError(string message)
	{
		robotManager.Helpful.Logging.WriteError("[Instancer] " + message);
	}

	public Instancer()
	{
		Name = "Instancer";
	}

	static Instancer()
	{
	}

	public class MapInfo
	{
		public string name;
		public int id;

		public MapInfo(int mapId, string mapName)
		{
			id = mapId;
			name = mapName;
		}

	}

}
