var barrel = ObjectManager.GetObjectWoWUnit().FirstOrDefault(u => u.IsValid && u.Entry == 115947
	&& u.GetDescriptorStartAddress > 0 && wManager.Wow.Memory.WowMemory.Memory.ReadUInt32(u.GetDescriptorStartAddress + (uint)wManager.Wow.Patchables.Descriptors.UnitFields.InteractSpellID) == 230877);

if (barrel == null)
	return true;

while (!barrel.IsGoodInteractDistance)
{
	MovementManager.MoveTo(barrel);
	Thread.Sleep(1 * 1000);
}

Interact.InteractGameObject(barrel.GetBaseAddress);
Thread.Sleep(3 * 1000);
return true;

