public class DarkbrulArenaPvP : QuestClass
{
	static int _questID = 41013;
	static Vector3 _jumpStartPosition = new Vector3(3274.385, 4128.003, 1021.494, "None");
	static Vector3 _jumpEndPosition = new Vector3(3304.977, 4125.66, 951.9113, "None");
	static Vector3 _arenaPosition = new Vector3(3315.515, 4110.982, 778.0323, "None");
	static int _featherstoneID = 246734;
	static uint _featherstoneBuffID = 202750;
	static float _arenaRadius = 200;
	static List<int> _mobs = new List<int>()
	{
		102623, // http://www.wowhead.com/npc=102623/stonefist-brawler
	};
	public DarkbrulArenaPvP()
	{
		Name = "Darkbrul Arena (PvP)";
		QuestId.Add(_questID);
		Step.AddRange(new[] { 10 });
	}
	void Log(string text)
	{
		Logging.WriteDebug("[" + Name + "] " + text);
	}
	public static bool InArena
	{
		get
		{
			return ObjectManager.Me.Position.Z - 5 < _jumpStartPosition.Z && ObjectManager.Me.Position.DistanceTo2D(_arenaPosition) < _arenaRadius;
		}
	}
	public override bool Pulse()
	{
		if (WorldQuest.CompletePvP(_questID))
		{
			Log("quest complete. get out from arena");
			WorldQuest.Whistle.Use();
			return true;
		}
		//if (ObjectManager.Me.Position.Z - 5 >= _jumpStartPosition.Z || ObjectManager.Me.Position.DistanceTo2D(_arenaPosition) > _arenaRadius)
		if (!InArena)
		{
			Log("goto jump start");
			if (GoToTask.ToPosition(_jumpStartPosition))
			{
				MountTask.DismountMount();
				Log("jump");
				ClickMove(_jumpEndPosition);
			}
			return true;
		}
		if (ObjectManager.Me.IsFalling)
		{
			Log("falling");
			Thread.Sleep(1 * 1000);
			return true;
		}
		if (ObjectManager.Me.Position.Z < _arenaPosition.Z + 20)
		{
			ChangeSettings();
			Log("kill");
			WorldQuest.TryKill(_arenaPosition, _mobs, 99, 100);
			return true;
		}
		if (!ObjectManager.Me.HaveBuff(_featherstoneBuffID))
		{
			Log("need featherstone buff");
			var featherstone = ObjectManager.GetNearestWoWGameObject(ObjectManager.GetWoWGameObjectByEntry(_featherstoneID));
			if (featherstone != null && featherstone.IsValid)
			{
				bool success = false;
				var path = PathFinder.FindPath(featherstone.Position, out success);
				if (!success)
				{
					wManager.wManagerSetting.AddBlackList(featherstone.Guid, 5 * 60 * 1000); //5 min blacklist
					return true;
				}
				Log("use featherstone for buff");
				GoToTask.ToPositionAndIntecractWithGameObject(featherstone.Position, featherstone.Entry);
				return true;
			}
		}
		Log("jump to arena");
		MountTask.DismountMount();
		ClickMove(_arenaPosition);
		return true;
	}
	void ClickMove(Vector3 p)
	{
		ClickToMove.CGPlayer_C__ClickToMove(p.X, p.Y, p.Z, MemoryRobot.Int128.Zero(), (int)ClickToMoveType.Move, 3.5f);
		Thread.Sleep(Others.Random(3, 5) * 1000);
	}
	void ChangeSettings()
	{
		var settings = wManager.wManagerSetting.CurrentSetting;
		if (settings.IgnoreFightWithPlayer)
		{
			settings.LootMobs = true;
			settings.LootChests = true;
			settings.IgnoreFightWithPlayer = false;
			Log("settings changed");
		}
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsCompleted()
	{
		return false;
	}
	public override bool IsComplete()
	{
		return WorldQuest.CompletePvP(_questID) && !InArena;
	}
}

