public class BattleforHighmountainHelper: QuestClass
{
	static Vector3 _top = new Vector3(4079.076, 4376.232, 768.6519, "None");
	public static Vector3 Bottom = new Vector3(4083.102, 4385.464, 670.6252, "None");
	public static Vector3 PositionBoss = new Vector3(4417.15, 4527.588, 899.9885, "None");
	static Vector3 _positionShipLanding = new Vector3(4374.185, 4613.346, 967.6221 - 20f, "None");
	static Vector3 _elevatorTopWait = new Vector3(4113.781, 4332.575, 768.277, "None");// new Vector3(4112.613, 4332.563, 768.2646, "None");
	static Vector3 _elevatorTop = new Vector3(4112.629, 4340.39, 768.4555 - 10f, "None");
	static Vector3 _elevatorBottomWait = new Vector3(4106.778, 4350.693, 667.8329, "None");
	static Vector3 _elevatorBottom = new Vector3(4112.629, 4340.39, 668.2487 + 10f, "None");
	static Vector3 _elevatorAtBottom = new Vector3(4111.773, 4341.093, 667.5279);
	static Vector3 _elevatorAtTop = new Vector3(4111.773, 4341.093, 767.7347);
	public static Vector3 PositionPortal = new Vector3(4414.168, 4528.487, 941.4249);
	public static Vector3 PositionEagles = new Vector3(4248, 4441, 673);
	public static int EagleID = 119857;
	public static int CageID = 119994;
	public static int DynamiteID = 119981;
	public static int PortaID = 120081;
	public static int BossID = 119579;
	static int _elevatorID = 243893;
	static void Log(string text)
	{
		Logging.WriteDebug("[Battle for Highmountain] " + text);
	}
	public static bool InScenario
	{
		get
		{
			return Usefuls.ContinentId == (int)ContinentId.LegionShipHorizontalHighMountain;
		}
	}
	public static bool InTop
	{
		get
		{
			var myPos = ObjectManager.Me.Position;
			return myPos.DistanceZ(_top) < 20 && myPos.DistanceTo2D(_top) < 100;
		}
	}
	public static bool InBottom
	{
		get
		{
			var myPos = ObjectManager.Me.Position;
			return myPos.DistanceTo2D(Bottom) < 100 && myPos.DistanceZ(Bottom) < 30;
		}
	}
	public static bool ToTop()
	{
		if (InTop)
		{
			Log("in Thundertotem top floor");
			return true;
		}
		Questing.UseTransport(_elevatorID,
			//from
			_elevatorAtBottom, _elevatorBottomWait, _elevatorBottom, ContinentId.LegionShipHorizontalHighMountain,
			//to
			_elevatorAtTop, _elevatorTopWait, ContinentId.LegionShipHorizontalHighMountain,
			1f);
		return false;
	}
	public static bool ToBottom()
	{
		if (InBottom)
		{
			Log("in Thundertotem bottom floor");
			return true;
		}
		Questing.UseTransport(_elevatorID,
			//from
			_elevatorAtTop, _elevatorTopWait, _elevatorTop, ContinentId.LegionShipHorizontalHighMountain,
			//to 
			_elevatorAtBottom, _elevatorBottomWait, ContinentId.LegionShipHorizontalHighMountain,
			1f);
		return false;
	}
	public static bool InShip
	{
		get
		{
			return ObjectManager.Me.Position.Z > PositionBoss.Z - 20;
		}
	}
	public static bool IsNearBoss
	{
		get
		{
			var myPos = ObjectManager.Me.Position;
			return myPos.DistanceZ(PositionBoss) < 20 && myPos.DistanceTo2D(PositionBoss) < 60;
		}
	}
	public BattleforHighmountainHelper()
	{
		Name = "Battle for Highmountain Helper";
		QuestId.Add(0);
		Step.Add(0);
	}
}

if (!BattleforHighmountainHelper.InBottom)
{
	BattleforHighmountainHelper.ToBottom();
	return true;
}
if (Questing.Attack(120060, 119860, 119959))
{
	return true;
}
GoToTask.ToPosition(BattleforHighmountainHelper.Bottom);
return true;

if (BattleforHighmountainHelper.InTop)
{
	BattleforHighmountainHelper.ToBottom();
	return true;
}
Questing.Escort(BattleforHighmountainHelper.Bottom, 119850, 5, 30, new List<int>() { 119986 });
return true;

if (ObjectManager.Me.PlayerUsingVehicle)
	return true;

if (!BattleforHighmountainHelper.InShip)
{
	GoToTask.ToPositionAndIntecractWithNpc(BattleforHighmountainHelper.PositionEagles, BattleforHighmountainHelper.EagleID, 1);
	return true;
}
var hotspots = new List<Vector3>()
{
	//top
	new Vector3(4393.5, 4573.92, 966.9947, "None"),
	new Vector3(4437.748, 4540.5, 960.7629, "None"),
	new Vector3(4385.224, 4514.247, 960.2782, "None"),
	new Vector3(4430.686, 4484.306, 967.0199, "None"),
	//bottom
	new Vector3(4388.909, 4535.954, 941.6694, "None"),
	new Vector3(4422.352, 4553.875, 941.6689, "None"),
	new Vector3(4439.03, 4520.34, 941.6696, "None"),
	new Vector3(4403.545, 4501.813, 941.6696, "None"),
	//bottom2
	new Vector3(4438.321, 4477.314, 932.3834, "None"),
	new Vector3(4390.028, 4580.496, 932.3842, "None"),
};
var p = hotspots[Others.Random(0, hotspots.Count - 1)];
//rescue
if (Questing.Scenario.Stage == 7)
{
	var cage = Questing.FindUnit(BattleforHighmountainHelper.CageID);
	if (cage != null && cage.IsValid)
	{
		GoToTask.ToPositionAndIntecractWithNpc(cage.Position, cage.Entry);
		return true;
	}
	GoToTask.ToPosition(p);
	return true;
}
//dynamites
if (Questing.Scenario.Stage == 8)
{
	var dynamit = Questing.FindUnit(BattleforHighmountainHelper.DynamiteID);
	if (dynamit != null && dynamit.IsValid)
	{
		GoToTask.ToPositionAndIntecractWithNpc(dynamit.Position, dynamit.Entry);
		return true;
	}
	GoToTask.ToPosition(p);
	return true;
}
if (!BattleforHighmountainHelper.IsNearBoss)
{
	GoToTask.ToPositionAndIntecractWithNpc(BattleforHighmountainHelper.PositionPortal, BattleforHighmountainHelper.PortaID, 1);
	return true;
}
if (Questing.Attack(BattleforHighmountainHelper.BossID))
	return true;


var eagle = Questing.FindUnit(120048);
if (eagle != null && eagle.IsValid && !eagle.GetMove)
	GoToTask.ToPositionAndIntecractWithNpc(eagle.Position, eagle.Entry);

return true;
