var falconId = 93064;
var campPosition = new Vector3(2724.639, 7401.729, 5.455719, "None");
var maxDist = 60;
var mobEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(falconId));
if (ObjectManager.Me.Position.DistanceTo(campPosition) > 50)
{
	//Logging.Write("too far from falcon camp");
	wManager.Wow.Bot.Tasks.GoToTask.ToPosition(campPosition);
	return true;
}

if (mobEntry != null && mobEntry.IsAlive && mobEntry.IsAttackable && mobEntry.IsValid)
{
	if (ObjectManager.Me.Position.DistanceTo(mobEntry.Position) < maxDist)
	{
		Interact.InteractGameObject(mobEntry.GetBaseAddress);
		Thread.Sleep(Usefuls.Latency);
		MovementManager.Face(mobEntry);
		//Logging.Write("falcons near. mark");
		wManager.Wow.Helpers.Lua.LuaDoString("ExtraActionButton1:Click()");
		Usefuls.WaitIsCasting();
		wManager.wManagerSetting.AddBlackList(mobEntry.Guid, 1000 * 1 * 60);
		return true;
	}
	//Logging.Write("no falcons near");
}
//Logging.Write("NO falcons at all");
return true;

var sheepId = 106009;
