var campPosition = new Vector3(2456.779, 7529.088, 30.133, "None");
var corpseId = 247962;
var mobId = 93237;

if (ObjectManager.Me.Position.DistanceTo(campPosition) > 100)
{
	GoToTask.ToPosition(campPosition);
	return true;
}
//ignore blacklist
var corpseEntry = ObjectManager.GetObjectWoWGameObject().FirstOrDefault(o => o.Entry == corpseId);
if (corpseEntry != null && corpseEntry.IsValid)
{
	if (corpseEntry.GetDistance > 7)
	{
		GoToTask.ToPosition(corpseEntry.Position);
		return true;
	}
	//GoToTask.ToPositionAndIntecractWithGameObject(corpseEntry.Position, corpseEntry.Entry);
	Interact.InteractGameObject(corpseEntry.GetBaseAddress);
	Thread.Sleep(3 * 1000);
	return true;
}
//ignore blacklist
var mobEntry = ObjectManager.GetObjectWoWUnit().FirstOrDefault(u => u.Entry == mobId);
if (mobEntry != null && mobEntry.IsValid && mobEntry.IsAlive && mobEntry.IsAttackable)
{
	Interact.InteractGameObject(mobEntry.GetBaseAddress);
	Fight.StartFight(mobEntry.Guid);
	return true;
}
return true;

