/* Fel Fire and Ice */
  if (WorldQuest.Has(47132))
  {
   Thread t = new Thread(() =>
   {
    while (robotManager.Products.Products.IsStarted)
    {
     if (Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause)
     {
      if (WorldQuest.Complete(47132))
       break;

      if (Quest.HasQuest(47132))
       Questing.UseExtraButton();
     }
     Thread.Sleep(9 * 1000);
    }
   });
   t.Start();
  }

