public class BrokenShoreConstructionBuff : QuestClass
{
	List<uint> _buffsID = new List<uint>()
	{
		// command center
		239966, // http://www.wowhead.com/spell=239966/war-effort
		240986, // http://www.wowhead.com/spell=240986/worthy-champions
		240989, // http://www.wowhead.com/spell=240989/heavily-augmented
		240987, // http://www.wowhead.com/spell=240987/well-prepared
		// mage tower
		237139, // http://www.wowhead.com/spell=237139/power-overwhelming
		237137, // http://www.wowhead.com/spell=237137/knowledgeable
		240980, // http://www.wowhead.com/spell=240980/light-as-a-feather
		240979, // http://www.wowhead.com/spell=240979/reputable
		// nether disruptor
		239967, // http://www.wowhead.com/spell=239967/seal-your-fate
		240985, // http://www.wowhead.com/spell=240985/reinforced-reins
		239968, // http://www.wowhead.com/spell=239968/fate-smiles-upon-you
		239969, // http://www.wowhead.com/spell=239969/netherstorm
	};
	List<int> _questsID = new List<int>()
	{
		// command center
		46998, // http://www.wowhead.com/quest=46998/boon-of-the-command-center
		46997, // http://www.wowhead.com/quest=46997/boon-of-the-command-center
		46904, // http://www.wowhead.com/quest=46904/boon-of-the-command-center
		46999, // http://www.wowhead.com/quest=46999/boon-of-the-command-center
		// mage tower
		47009, // http://www.wowhead.com/quest=47009/boon-of-the-mage-tower
		47007, // http://www.wowhead.com/quest=47007/boon-of-the-mage-tower
		47008, // http://www.wowhead.com/quest=47008/boon-of-the-mage-tower
		47010, // http://www.wowhead.com/quest=47010/boon-of-the-mage-tower
		// nether disruptor
		47016, // http://www.wowhead.com/quest=47016/boon-of-the-nether-disruptor
		47015, // http://www.wowhead.com/quest=47015/boon-of-the-nether-disruptor
		47012, // http://www.wowhead.com/quest=47012/boon-of-the-nether-disruptor
		47014, // http://www.wowhead.com/quest=47014/boon-of-the-nether-disruptor
	};
	List<int> _requiredQuestsID = new List<int>(){
		46773, // http://www.wowhead.com/quest=46773/the-command-center
		46772, // http://www.wowhead.com/quest=46772/the-mage-tower
		46774, // http://www.wowhead.com/quest=46774/the-nether-disruptor
	};
	Vector3 _npcPosition = new Vector3(-1625.09, 3201.695, 129.0469, "None");
	int _npcEntry = 120183;
	int _attemptsMax = 0;
	public BrokenShoreConstructionBuff()
	{
		Name = "+Broken Shore Construction Buff";
		QuestId.AddRange(_questsID);
		Step.AddRange(new[] { 0 });
		_attemptsMax = 2;
	}
	void Log(string text)
	{
		Logging.WriteDebug("[" + Name + "] " + text);
	}
	public override bool Pulse()
	{
		GoToTask.ToPositionAndIntecractWithNpc(_npcPosition, _npcEntry);
		Thread.Sleep(Others.Random(500, 1500));
		Quest.CompleteQuest();
		_attemptsMax -= 1;
		Log("trying to get construction buff. attempts left=" + _attemptsMax);
		return true;
	}
	public override bool IsComplete()
	{
		//no requirements
		if (!CanConditions())
			return true;

		//too far
		if (ObjectManager.Me.Position.DistanceTo2D(_npcPosition) > 100)
			return true;

		if (ObjectManager.Me.HaveBuff(_buffsID))
			return true;

		return _attemptsMax <= 0;
	}
	public override bool CanConditions()
	{
		return Quest.GetQuestCompleted(_requiredQuestsID);
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsCompleted()
	{
		return IsComplete();
	}
}

