public static class BrokenShoreHelper
{
	static Vector3 portalPosition = new Vector3(-1433.822, 3018.01, 36.48209, "None");
	static Vector3 portalPosition2 = new Vector3(-1425.365, 3029.871, 34.54687, "None");

	static void Log(string text)
	{
		Logging.WriteDebug("[Broken Shore Helper] " + text);
	}

	public static bool GotoPortal(Vector3 p0, Vector3 p)
	{
		if (ObjectManager.Me.Position.DistanceTo(p0) < 5)
		{
			Log("goto portal " + p);
			ClickToMove.CGPlayer_C__ClickToMove(p.X, p.Y, p.Z, MemoryRobot.Int128.Zero(), (int)ClickToMoveType.Move, 0.5f);
			Thread.Sleep(5 * 1000);
			return true;
		}
		Log("goto near portal " + p0);
		GoToTask.ToPosition(p0);
		return true;
	}

	public static bool GoToPortal()
	{
		return GotoPortal(portalPosition, portalPosition2);
	}

	public static class DivingHelmet
	{
		static robotManager.Helpful.Timer _timer = new robotManager.Helpful.Timer(2 * 60 * 1000);
		static MemoryRobot.Int128 _lastGuid = MemoryRobot.Int128.Zero();
		static uint _buffDamagedDivingHelmet = 245498;
		static int _npcDamagedDivingHelmet = 123230;
		static Vector3 startPosition = new Vector3(-1820.049, 2778.26, -2.197452, "Swimming");
		static List<uint> aquaticMounts = new List<uint>()
		{
			223018, // http://www.wowhead.com/spell=223018/fathom-dweller
			228919, // http://www.wowhead.com/spell=228919/darkwater-skate
			214791, // http://www.wowhead.com/spell=214791/brinedeep-bottom-feeder
			64731, // http://www.wowhead.com/spell=64731/sea-turtle
			30174, // http://www.wowhead.com/spell=30174/riding-turtle
			98718, // http://www.wowhead.com/spell=98718/subdued-seahorse
		};

		public static bool Get()
		{
			wManager.wManagerSetting.CurrentSetting.UseMount = false;
			wManager.wManagerSetting.CurrentSetting.UseGroundMount = false;
			wManager.wManagerSetting.CurrentSetting.UseFlyingMount = false;
			wManager.wManagerSetting.CurrentSetting.AquaticMountName = "";
			if (!ObjectManager.Me.HaveBuff(_buffDamagedDivingHelmet))
			{
				var helmet = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(_npcDamagedDivingHelmet));
				if (helmet != null && helmet.IsValid)
				{
					if (_lastGuid != helmet.Guid)
					{
						Log("try get helmet: " + helmet.Name);
						_lastGuid = helmet.Guid;
						_timer.Reset();
					}
					else if (_timer.IsReady)
					{
						Log("can't reach helmet: " + helmet.Name + ". Blacklist it");
						wManager.wManagerSetting.AddBlackList(helmet.Guid, 3 * 60 * 1000); //5 min blacklist
						_lastGuid = MemoryRobot.Int128.Zero();
						return true;
					}
					MountTask.DismountMount();
					Log("goto helmet");
					if (GoToTask.ToPosition(helmet.Position))
					{
						MountTask.DismountMount();
						Log("use helmet");
						GoToTask.ToPositionAndIntecractWithNpc(helmet.Position, helmet.Entry);
					}
				}
				else
				{
					wManager.wManagerSetting.CurrentSetting.UseMount = true;
					wManager.wManagerSetting.CurrentSetting.UseGroundMount = true;
					wManager.wManagerSetting.CurrentSetting.UseFlyingMount = true;
					Log("to start");
					if (GoToTask.ToPosition(startPosition))
					{
						MountTask.DismountMount();
					}
				}
				return true;
			}
			return false;
		}
	}

}

