public class DumpLegionfallWarSupplies : QuestClass
{
	int _attemptsMax = 0;
	int _maxAmount = 900;
	string _lua = @"
local activeContributions = {C_ContributionCollector.GetActive()}
for i = 1,#activeContributions do
	local contributionID = activeContributions[i]
	local state = C_ContributionCollector.GetState(contributionID)
	local pending = C_ContributionCollector.HasPendingContribution(contributionID)
	if not pending and state == 1 then
		C_ContributionCollector.Contribute(contributionID)
		return contributionID
	end
end
return 0
";
	public DumpLegionfallWarSupplies()
	{
		Name = "+Dump Legionfall War Supplies";
		QuestId.Add(0);
		Step.AddRange(new[] { 0 });
		if (LegionfallWarSupplies >= _maxAmount)
			_attemptsMax = 10;
	}
	void Log(string text)
	{
		Logging.WriteDebug("[" + Name + "] " + text);
	}
	public override bool Pulse()
	{
		var p = new Vector3(-1621.912, 3202.07, 129.0166, "None");
		GoToTask.ToPositionAndIntecractWithNpc(p, 119305);
		Thread.Sleep(Others.Random(1000, 2000));
		var frameName = "ContributionCollectionFrame";
		if (Lua.LuaDoString<bool>("return " + frameName + ":IsVisible()"))
		{

			var contributorID = Lua.LuaDoString<int>(_lua);
			_attemptsMax -= 1;
			Log("contributed to :" + contributorID + " attempts left=" + _attemptsMax);
			Thread.Sleep(Others.Random(1000, 2000));
		}
		return true;
	}
	public override bool IsComplete()
	{
		if (!WorldQuest.WorldQuestSettings.CurrentSetting.DoDumpLegionfallWarSupplies)
			return true;

		return _attemptsMax <= 0;
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsCompleted()
	{
		return false;
	}
	static int LegionfallWarSupplies
	{
		get
		{
			return Lua.LuaDoString<int>("local _, level = GetCurrencyInfo('currency: 1342'); return level;");
		}
	}
}

