public class AGiantProblem2Group : AGiantProblem1Group
{
	robotManager.Helpful.Timer _timer = new robotManager.Helpful.Timer(2 * 60 * 1000);
	MemoryRobot.Int128 _lastGuid = MemoryRobot.Int128.Zero();

	public AGiantProblem2Group()
	{
		Name = "A Giant Problem 2 (Group)";
	}
	public override bool Pulse()
	{
		if (BrokenShoreHelper.DivingHelmet.Get())
			return true;

		var cargos = new List<int>() {
			267575,
		};
		var cargo = ObjectManager.GetNearestWoWGameObject(ObjectManager.GetWoWGameObjectByEntry(cargos));
		if (cargo != null && cargo.IsValid)
		{
			if (_lastGuid != cargo.Guid)
			{
				Logging.Write("try get cargo: " + cargo.Name);
				_lastGuid = cargo.Guid;
				_timer.Reset();
			}
			else if (_timer.IsReady)
			{
				Logging.Write("can't reach cargo: " + cargo.Name + ". Blacklist it");
				wManager.wManagerSetting.AddBlackList(cargo.Guid, 3 * 60 * 1000); //5 min blacklist
				_lastGuid = MemoryRobot.Int128.Zero();
				return true;
			}
			GoToTask.ToPositionAndIntecractWithGameObject(cargo.Position, cargo.Entry);
		}
		var hotspots = new List<Vector3>() {
			startPosition,
			new Vector3(-1883.56, 2685.059, -1.593838, "Swimming"),
			new Vector3(-1940.298, 2690.946, -1.594494, "Swimming"),
			new Vector3(-1975.861, 2695.668, -1.614278, "Swimming"),
			new Vector3(-1863.423, 2739.264, -1.559397, "Swimming"),
			new Vector3(-1867.487, 2788.262, -1.537572, "Swimming"),
			new Vector3(-1873.963, 2836.216, -1.544325, "Swimming"),
			new Vector3(-1927.868, 2858.48, -1.578439, "Swimming"),
		};
		GoToTask.ToPosition(hotspots[Others.Random(0, hotspots.Count -1)], 5, false,
			(c)=> ObjectManager.GetNearestWoWGameObject(ObjectManager.GetWoWGameObjectByEntry(cargos)) == null
		);
		return true;
	}
	public override bool IsComplete()
	{
		return WorldQuest.CompleteGroup(_questID, 2);
	}
}

