public sealed class ByWaterBePurged : QuestClass
{
	List<int> _mobs = new List<int>() {
			120103,
			120112,
			120113,
	};
	List<Vector3> _path = new List<Vector3>() {
		new Vector3(-1379.059f, 2589.032f, 46.65727f, "None"),
		new Vector3(-1368.6f, 2585.881f, 46.65727f, "None"),
		new Vector3(-1354.491f, 2584.056f, 46.45698f, "None"),
		new Vector3(-1339.254f, 2583.02f, 45.90431f, "None"),
		new Vector3(-1324.75f, 2587.029f, 46.23001f, "None"),
		new Vector3(-1307.598f, 2586.237f, 47.61461f, "None"),
		new Vector3(-1293.562f, 2588.457f, 44.04774f, "None"),
		new Vector3(-1278.571f, 2591.469f, 42.43921f, "None"),
		new Vector3(-1265.391f, 2590.064f, 40.67246f, "None"),
		new Vector3(-1250.226f, 2591.236f, 40.09552f, "None"),
		new Vector3(-1235.121f, 2592.407f, 39.73841f, "None"),
		new Vector3(-1220.558f, 2593.57f, 37.90187f, "None"),
		new Vector3(-1205.474f, 2597.681f, 36.99294f, "None"),
		new Vector3(-1191.159f, 2603.884f, 32.98927f, "None"),
