		new Vector3(-1314.476f, 2667.092f, 36.68215f, "None"),
		new Vector3(-1320.917f, 2659.597f, 39.48019f, "None"),
		new Vector3(-1327.217f, 2650.28f, 41.36594f, "None"),
		new Vector3(-1333.863f, 2642.238f, 42.16872f, "None"),
	};
	static float _minDist = 55f;
	public ByWaterBePurged()
	{
		Name = "By Water Be Purged";
		QuestId.Add(46201);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
	}
	public override bool CanConditions()
	{
		return true;
	}
	public override bool HasQuest()
	{
		return WorldQuest.Has(this);
	}
	public override bool IsComplete()
	{
		return WorldQuest.Complete(this);
	}
	public override bool Pulse()
	{
		if (TryBecomeBiker())
			return true;

		if (TryCombat())
			return true;

		TryFind(_mobs, _path);
		return true;
	}

	bool TryBecomeBiker()
	{
		if (ObjectManager.Me.PlayerUsingVehicle)
			return false;

		Log("mount vehicle");
		var p = new Vector3(-1463.297, 2472.944, 1.250999, "None");
		GoToTask.ToPositionAndIntecractWithNpc(p, 120085, 1);
		return true;
	}

	public static bool TryFind(List<int> mobs, List<Vector3> path)
	{
		var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mobs));
		if (mob != null && mob.IsAlive && mob.IsAttackable && mob.IsValid)
		{
			Log("found mob " + mob.Name);
			Interact.InteractGameObject(mob.GetBaseAddress, false, false, true);
			Thread.Sleep(Usefuls.Latency + 150);
			return true;
		}
		if (!MovementManager.InMovementLoop)
		{
			var closest = path.OrderBy(v => ObjectManager.Me.Position.DistanceTo(v)).FirstOrDefault();
			Log("loop path from " + closest);
			GoToTask.ToPosition(closest);
			MovementManager.GoLoop(path);
			return true;
		}
		return true;
	}
	public static bool TryCombat()
	{
		var target = ObjectManager.GetUnitAttackPlayer().FirstOrDefault();
		if (target == null)
			target = ObjectManager.Me.TargetObject;

		if (target == null || !target.IsAlive || !target.IsAttackable || !target.IsValid)
			return false;

		Interact.InteractGameObject(target.GetBaseAddress, false, false, true);
		var targetP = target.Position;
		var dist = ObjectManager.Me.Position.DistanceTo(targetP);
		if (dist > _minDist)
		{
			Log("get close to " + target.Name);
			GoToTask.ToPosition(targetP, _minDist);
			return true;
		}
		MovementManager.StopMove();

		//uint spellFilterFel = 239331;
		if (ObjectManager.Pet.HealthPercent < 50 && !ObjectManager.Pet.InCombat && Questing.NoCooldownSpell(239331))
		{
			Log("use heal #5");
			Questing.Vehicle.UseButton(5);
			Usefuls.WaitIsCasting();
			return true;
		}
		//uint spellThrowGlobule = 239284;
		if (dist < 55 && Questing.NoCooldownSpell(239284))
		{
			Log("use attack #2");
			MovementManager.Face(targetP);
			Thread.Sleep(Usefuls.Latency * 2);
			Questing.Vehicle.UseButton(2);
			Thread.Sleep(Usefuls.Latency * 2);
			ClickOnTerrain.Pulse(target.Position);
			Usefuls.WaitIsCasting();
			return true;
		}
		//uint spellRoaringTorrent = 239277;
		if (dist < 30 && Questing.NoCooldownSpell(239277))
		{
			Log("use attack #3");
			MovementManager.Face(targetP);
			Thread.Sleep(Usefuls.Latency * 2);
			Questing.Vehicle.UseButton(3);
			Usefuls.WaitIsCasting();
			return true;
		}
		//uint spellDrenchingPunch = 239310;
		if (dist < 5 && Questing.NoCooldownSpell(239310))
		{
			Log("use attack #1");
			MovementManager.Face(targetP);
			Thread.Sleep(Usefuls.Latency * 2);
			Questing.Vehicle.UseButton(1);
			Usefuls.WaitIsCasting();
			return true;
		}
		return false;
	}
	static void Log(string text)
	{
		Logging.WriteDebug("[By Water Be Purged] " + text);
	}
}

