var campPosition = new Vector3(-726.9622, 2697.221, 77.68881, "None");
var mobs = new List<int>() {
	118776,
	118787,
	118819,
};
WorldQuest.TryKillElite(campPosition, mobs);
return true;

var minDist = 4f;
var maxDist = 7.9f;
var hotspots = new List<Vector3>()
{
	new Vector3(-1583.442, 3801.887, 29.51741, "None"),
	new Vector3(-1610.353, 3727.104, 24.58605, "None"),
	new Vector3(-1539.866, 3675.893, 19.62704, "None"),
	new Vector3(-1510.456, 3748.817, 24.46718, "None")
};
var deltas = new List<Vector3>()
{
	new Vector3(0, 0, 0) * minDist,
	new Vector3(1, 0, 0) * minDist,
	new Vector3(-1, 0, 0) * minDist,
	new Vector3(0, 1, 0) * minDist,
	new Vector3(0, -1, 0) * minDist,
	new Vector3(1, 1, 0) * minDist,
	new Vector3(-1, 1, 0) * minDist,
	new Vector3(1, -1, 0) * minDist,
	new Vector3(-1, -1, 0) * minDist,
};
var nest = ObjectManager.GetNearestWoWGameObject(ObjectManager.GetWoWGameObjectByEntry(267778));
if (nest != null && nest.IsValid)
{
	if (!Questing.CheckGUID(nest.Guid, 3, 5))
	{
		Logging.WriteDebug("[Fel-Corrupted Feathers] too long, blacklist");
		return true;
	}
	if (nest.GetDistance < maxDist)
	{
		Logging.WriteDebug("[Fel-Corrupted Feathers] use " + nest.Name);
		MountTask.DismountMount();
		Thread.Sleep(Usefuls.Latency * 2);
		Interact.InteractGameObject(nest.GetBaseAddress, true, false);
		if (!wManager.wManagerSetting.IsBlackListed(nest.Guid))
			wManager.wManagerSetting.AddBlackList(nest.Guid, 5 * 60 * 1000, true);

		return true;
	}
	var result = false;
	foreach (var delta in deltas)
	{
		var p = nest.Position + delta;
		var path = PathFinder.FindPath(p, out result);
		if (result)
		{
			Logging.WriteDebug("[Fel-Corrupted Feathers] found good landpoint " + nest.Position.ToStringNewVector() + " + " + delta.ToStringNewVector());
			GoToTask.ToPosition(p);
			return true;
		}
	}
	if (!wManager.wManagerSetting.IsBlackListed(nest.Guid))
	{
		Logging.WriteDebug("[Fel-Corrupted Feathers] blacklist " + nest.Name + " at " + nest.Position.ToStringNewVector());
		wManager.wManagerSetting.AddBlackList(nest.Guid, 5 * 60 * 1000, true);
	}
	return true;
}
var p0 = hotspots[Others.Random(0, hotspots.Count - 1)];
Logging.WriteDebug("[Help on the Way] searching at " + p0.ToStringNewVector());
GoToTask.ToPosition(p0);
return true;

