public class KrakenEggs : QuestClass
{
	Vector3 _startPosition = new Vector3(-1962.385, 2815.822, -1.815495, "Swimming");
	Vector3 _npcPos = new Vector3(-1967.997, 2804.472, -50.67209, "Swimming");
	int _npcID = 120725;
	uint _buffID = 240472;
	float _minDistance = 18;
	//_egg
	int _eggID = 118184;

	List<Vector3> _eggSpots = new List<Vector3>() {
		new Vector3(-1892.772f, 2750.251f, -8.148987f, "Swimming"),
		new Vector3(-1912.513f, 2725.865f, -12.9394f, "Swimming"),
		new Vector3(-1942.979f, 2720.386f, -16.62297f, "Swimming"),
		new Vector3(-1957.246f, 2748.739f, -20.68436f, "Swimming"),
		new Vector3(-1981.353f, 2768.17f, -22.03741f, "Swimming"),
		new Vector3(-1997.789f, 2793.504f, -21.81253f, "Swimming"),
		new Vector3(-2007.203f, 2822.53f, -23.45504f, "Swimming"),
		new Vector3(-2015.202f, 2851.93f, -25.89667f, "Swimming"),
	};
	robotManager.Helpful.Timer _timer = new robotManager.Helpful.Timer(2 * 60 * 1000);
	MemoryRobot.Int128 _lastGuid = MemoryRobot.Int128.Zero();

	public KrakenEggs()
	{
		Name = "Kraken Eggs";
		QuestId.Add(45472);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
	}
	public override bool Pulse()
	{
		if (BrokenShoreHelper.DivingHelmet.Get())
			return true;

		if (!ObjectManager.Me.HaveBuff(_buffID))
		{
			if (GoToTask.ToPosition(_npcPos))
			{
				MountTask.DismountMount();
				return GoToTask.ToPositionAndIntecractWithNpc(_npcPos, _npcID, 1);
			}
			return true;
		}
		var egg = Egg();
		if (egg != null && egg.IsValid)
		{
			Interact.InteractGameObject(egg.GetBaseAddress, false, false, true);
			if (_lastGuid != egg.Guid)
			{
				_lastGuid = egg.Guid;
				_timer.Reset();
			}
			else if (_timer.IsReady)
			{
				wManager.wManagerSetting.AddBlackList(egg.Guid, 3 * 60 * 1000, true);
				_lastGuid = MemoryRobot.Int128.Zero();
				return true;
			}
			if (egg.GetDistance > _minDistance)
			{
				return GoToTask.ToPosition(egg.Position, _minDistance);
			}
			if (Lua.LuaDoString<bool>("return HasExtraActionBar()"))
			{
				MovementManager.Face(egg);
				Thread.Sleep(Usefuls.Latency);
				Move.Forward();
				MovementManager.StopMove();
				Lua.LuaDoString("ExtraActionButton1:Click()");
				Usefuls.WaitIsCasting();
			}
			return true;
		}
		return GoToTask.ToPosition(_eggSpots[Others.Random(0, _eggSpots.Count - 1)], 5, false,
			(c) => Egg() == null
		);
	}
	WoWUnit Egg()
	{
		return ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(_eggID));
	}
	public override bool IsComplete()
	{
		return WorldQuest.Complete(this);
	}
	public override bool HasQuest()
	{
		return true;
	}
}

