	public RollingThunder()
	{
		Name = "Rolling Thunder";
		QuestId.Add(46175);
		Step.AddRange(new[] { 1 });
	}
	static string LastLog = string.Empty;
	static void Log(string text)
	{
		if (string.Equals(text, LastLog))
			return;

		LastLog = text;
		Logging.Write("[Rolling Thunder] " + LastLog);
	}
	public override bool Pulse()
	{
		if (!ObjectManager.Me.PlayerUsingVehicle)
		{
			PathIndex = 0;
			Log("mount barrel");
			if (GoToTask.ToPosition(StartPosition, MaxDist))
			{
				GoToTask.ToPositionAndIntecractWithNpc(StartPosition, 118875, 1);
			}
		}
		Thread.Sleep(Usefuls.Latency);
		if (!ObjectManager.Me.PlayerUsingVehicle)
		{
			Log("failed mount barrel");
			return true;
		}
		MovementManager.Go(path);
		do
		{
			Log("move left=" + ObjectManager.Me.Position.DistanceTo(EndPosition));
			Thread.Sleep(100);
		}
		while (MovementManager.InMovement && Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause && !Conditions.IsAttackedAndCannotIgnore && MoveCondition());
		Log("move done");
		return true;
		/*//
		var next = EndPosition;
		var myPos = ObjectManager.Me.Position;
		var near = path.Where(p => path.IndexOf(p) > PathIndex).OrderBy(v => v.DistanceTo(myPos)).FirstOrDefault();
		if (near != null && near != Vector3.Zero)
		{
			PathIndex = path.IndexOf(near);
			next = near;
		}
		else
		{
			Log("path end reached");
			PathIndex = path.Count;
		}
		if (myPos.DistanceTo(near) < MaxDist)
		{
			Log("move to near #" + PathIndex + "=" + near.ToStringNewVector());
			MovementManager.Go(path);
			do
			{
				Thread.Sleep(100);
			}
			while (MovementManager.InMovement && Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause && !Conditions.IsAttackedAndCannotIgnore && MoveCondition());
		}
		else
		{
			Log("go to near #" + PathIndex + "=" + near.ToStringNewVector());
			GoToTask.ToPosition(next, MinDist, false, MoveCondition);
		}
		return true;
		//*/
	}
	static float MinDist = 5f;
	static float MaxDist = 10f;
	static int _pathIndex = 0;
	static int PathIndex
	{
		get
		{
			return _pathIndex;
		}
		set
		{
			if (value <= 0)
				value = 0;
			else if (value >= path.Count)
				value = path.Count - 1;

			_pathIndex = value;
			Conditions.ForceIgnoreIsAttacked = (_pathIndex != 0);
		}
	}
	static bool MoveCondition(object c = null)
	{
		if (!ObjectManager.Me.PlayerUsingVehicle)
		{
			Log("dismounted. restart");
			return false;
		}
		return true;
	}
	public override bool IsComplete()
	{
		return WorldQuest.Complete(this);
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsCompleted()
	{
		return false;
	}
}

