public class SetYouUptheBomb : QuestGrinderClass
{
	Vector3 _startPosition = new Vector3(-706.821, 3508.536, 11.39637, "None");
	bool _buffStacked = false;
	uint _buffID = 233788;
	int _minBuffStack = 35;

	public SetYouUptheBomb()
	{
		Name = "Set You Up the Bomb!";
		QuestId.Add(45626);
		Step.AddRange(new[] { 10, 10, 50, 1 });
		EntryTarget.Add(117354);
		EntryTarget.Add(117425);
		HotSpots.Add(new Vector3(-732.0664, 3492.83, 15.99847, "None"));
		HotSpots.Add(new Vector3(-713.1297, 3477.629, 19.19549, "None"));
