		HotSpots.Add(new Vector3(-796.3351, 3447.987, 40.83015, "None"));
		HotSpots.Add(new Vector3(-617.3666, 3430.276, 12.97547, "None"));
	}
	static void Log(string text)
	{
		Logging.WriteDebug("[Set You Up the Bomb!] " + text);
	}
	public override bool Pulse()
	{
		Log("buffed=" + _buffStacked + " min=" + _minBuffStack);
		if (!_buffStacked)
		{
			_buffStacked = ObjectManager.Me.BuffStack(_buffID) >= _minBuffStack;
			GoToTask.ToPosition(_startPosition);
			return true;
		}
		_buffStacked = ObjectManager.Me.BuffStack(_buffID) > 5;
		return base.Pulse();
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsCompleted()
	{
		return false;
	}
	public override bool IsComplete()
	{
		return WorldQuest.Complete(this);
	}
}

