public class SivashRaid : QuestClass
{
	static int _questID = 46945;
	static Thread _thread = null;
	static List<int> _blacklistMobs = new List<int>() { 117487, };
	static List<int> _mobs = new List<int>()
	{
		117470,//sivash
	};
	static Vector3 _campPosition = new Vector3(-823.2843, 1540.843, 4.472394, "None");
	public SivashRaid()
	{ 
		Name = "Si'vash (Raid)";
		QuestId.Add(_questID);
		Step.Add(1);
	}
	public override bool Pulse()
	{
		WorldQuest.TryKillRaid(_campPosition, _mobs);
		return true;
	}
	static void StartFixSivashCombat()
	{
		if (_thread != null)
			return;

		StopFixSivashCombat();
		wManager.wManagerSetting.AddBlackListNpcEntry(117487, true); /* Ignore Guards */
		_thread = new Thread(() =>
		{
			while (robotManager.Products.Products.IsStarted)
			{
				if (Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause)
				{
					if (WorldQuest.Complete(_questID))
					{
						Log("combat fix ended");
						break;
					}

					var targ = ObjectManager.Target;
					if (targ != null && targ.IsValid && targ.IsAlive && targ.IsAttackable && _blacklistMobs.Contains(targ.Entry))
					{
						Fight.StopFight();
						var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(_mobs));
						if (mob != null && mob.IsValid && mob.IsAlive && mob.IsAttackable)
						{
							Log("combat switch to " + mob.Name);
							Fight.StartFight(mob.Guid);
						}
					}
				}
				Thread.Sleep(1000);
			}
		});
		_thread.Start();
		Log("combat fix started");
	}
	static void StopFixSivashCombat()
	{
		if (_thread == null)
			return;

		_thread.Abort();
		_thread = null;
		Log("combat fix stoped");
	}
	public override bool IsComplete()
	{
		return WorldQuest.CompleteRaid(_questID);
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsCompleted()
	{
		return false;
	}
	static void Log(string text)
	{
		Logging.WriteDebug("[Si'vash] " + text);
	}
}

var campPosition = new Vector3(-795.4545, 2325.487, 104.0382, "None");
var mobs = new List<int>();
if (!WorldQuest.Complete(this, 2))
{
	mobs.Add(118342 /* xargraxach */);
}
else
{
	mobs.Add(118422 /* wrathblade-honor-guard */);
}
WorldQuest.TryKillAnyElite(campPosition, mobs, 15);
return true;
