public class SecuringaFoothold : QuestClass
{
	public SecuringaFoothold()
	{
		Name = "Securing a Foothold";
		QuestId.Add(48200);
		Step.AddRange(new[] { 0 });
	}
	public override bool Pulse()
	{
		if (ArgusHelper.InAntoranWastesOpen)
		{
			var p0 = new Vector3(-2930.055, 8795.756, -232.1096, "None");
			Questing.Gather(p0, 275101, 10);
			return true;
		}
		if (ArgusHelper.InVendicarAntoranWastes)
		{
			var p = new Vector3(-2634.782, 8702.62, -70.81866, "None");
			GoToTask.ToPositionAndIntecractWithNpc(p, 121263, 1);
			return true;
		}
		ArgusHelper.ToVendicarAntoranWastes();
		return true;
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 2);
	}
	public override bool PickUp()
	{
		if (!ArgusHelper.InVendicarAntoranWastes)
		{
			ArgusHelper.ToVendicarAntoranWastes();
			return true;
		}
		return base.PickUp();
	}
	public override bool TurnIn()
	{
		if (!ArgusHelper.InAntoranWastesOpen)
		{
			ArgusHelper.ToAntoranWastesOpen();
			return true;
		}
		return base.TurnIn();
	}
}

