public class SizingUpTheOpposition : QuestClass
{
	public SizingUpTheOpposition()
	{
		Name = "Sizing Up The Opposition";
		QuestId.Add(48929); //Veiled Den
		QuestId.Add(47473); //Lights Purchase
		Step.AddRange(new[] { 0 });
	}
	static void Log(string text)
	{
		Logging.WriteDebug("[Sizing Up The Opposition] " + text);
	}
	void FixIDs()
	{
		if (QuestId.Count <= 1)
			return;

		if (ArgusHelper.OpenedVeiledDen)
		{
			Log("switch to Veiled Den");
			QuestId.Remove(47473);
		}
		else
		{
			Log("switch to Lights Purchase");
			QuestId.Remove(48929);
		}
	}
	public override bool PickUp()
	{
		FixIDs();
		if (!ArgusHelper.InAntoranWastesOpen)
		{
			Log("pickup in antoran wastes");
			ArgusHelper.ToAntoranWastesOpen();
			return true;
		}
		ReinforceFork.MoveTo(NpcPickUp.Position);
		return base.PickUp();
	}
	public override bool TurnIn()
	{
		FixIDs();
		if (ArgusHelper.InAntoranWastesOpen)
		{
			var teleporter = ArgusHelper.Teleporter.GetNear();
			if (teleporter == null)
			{
				Log("unable to find teleporter in Antoran wastes");
				return true;
			}
			ReinforceFork.MoveTo(teleporter.position);
			if (ObjectManager.Me.Position.DistanceTo(teleporter.position) > 30)
				return true;
		}
		if (!ArgusHelper.InVendicarAntoranWastes)
		{
			Log("to Antoran wastes Vendicar");
			ArgusHelper.ToVendicarAntoranWastes();
			return true;
		}
		Log("turn in");
		return base.TurnIn();
	}
}

