public static class EmpoweredArtifactHelper
{
	static Vector3 _PurpleParlorCenter = new Vector3(-801.2747, 4681.938, 930.7181, "None");

	public static bool InPurpleParlor
	{
		get
		{
			if (Usefuls.ContinentId != (int)ContinentId.Troll_Raid)
				return false;

			var myPos = ObjectManager.Me.Position;
			if (myPos.DistanceTo2D(_PurpleParlorCenter) > 50)
				return false;

			return myPos.DistanceZ(_PurpleParlorCenter) < 30;
		}
	}
	public static bool IsOutPurpleParlor()
	{
		if (!InPurpleParlor)
			return true;

		GoToTask.ToPositionAndIntecractWithGameObject(_PurpleParlorCenter, 266129);
		return false;
	}

	public class QuestStart : QuestClass
	{
		public List<WoWClass> AllowedClasses = new List<WoWClass>() { };
		protected int gossipAvailableQuests = 100;
		public QuestStart()
		{
			Step.Add(0);
		}
		protected void Log(string text)
		{
			Logging.WriteDebug("["+Name+"] " + text);
		}
		public override bool CanConditions()
		{
			return AllowedClasses.Contains(ObjectManager.Me.WowClass);
		}
		public override bool IsCompleted()
		{
			if (gossipAvailableQuests < 1)
				return true;

			//restricted for classes
			if (CanConditions())
				return base.IsCompleted();

			//other classes mark this quest as completed
			return true;
		}
		public override bool PickUp()
		{
			var npc = NpcPickUp;
			if (npc != null)
			{
				if (GoToTask.ToPositionAndIntecractWithNpc(npc.Position, npc.Id))
				{
					gossipAvailableQuests = Quest.GetNumGossipAvailableQuests();
				}
				return false;
			}
			return base.PickUp();
		}
	}
}

EasyQuest.IsCompleteCondition:
var p = new Vector3(-800.6981, 4681.194, 930.7178, "None");
var myP = ObjectManager.Me.Position;
return myP.DistanceTo2D(p) < 30 && myP.DistanceZ(p) < 10;

