public class TheThievingApprentice : EmpoweredArtifactHelper.QuestStart
{
	List<WoWClass> wowClasses = new List<WoWClass>()
	{
		WoWClass.Warrior,
		WoWClass.DeathKnight,
		WoWClass.DemonHunter,
		WoWClass.Rogue,
		WoWClass.Hunter,
	};
	public TheThievingApprentice()
	{
		Name = "The Thieving Apprentice";
		QuestId.AddRange(new[] { 44914, 47046, 47043, 47048, 47047 });
		AllowedClasses.AddRange(new WoWClass[] { WoWClass.Warrior, WoWClass.DeathKnight, WoWClass.DemonHunter, WoWClass.Rogue, WoWClass.Hunter, });
	}
	public override bool Pulse()
	{
		Traveler.ToKalimdor();
		return true;
	}
	public override bool IsComplete()
	{
		return Usefuls.ContinentId == (int)ContinentId.Kalimdor && !Traveler.InOrgrimmarCleftOfShadow;
	}
}

