public class TheArchmageAccosted : QuestClass
{
	static int _questID = 44924;
	static bool _isFightFixed = false;
	public TheArchmageAccosted()
	{
		Name = "The Archmage Accosted";
		QuestId.Add(_questID);
		Step.AddRange(new[] { 1, 1, 1 });
	}
	static void Log(string text)
	{
		Logging.WriteDebug("[The Archmage Accosted] " + text);
	}
	public override bool Pulse()
	{
		FixFightStart();
		if (!Questing.ObjectiveComplete(this, 1))
		{
			Log("#1");
			var p = new Vector3(3982.923, -4762.609, 304.806);
			if (!Questing.Attack(115860))
			{
				GoToTask.ToPosition(p);
			}
			return true;
		}
		var p2 = new Vector3(3984.784, -4766.185, 304.7669, "None");
		Questing.Gather(p2, 266034);
		return true;
	}
	static void FixFightStart()
	{
		if (_isFightFixed)
			return;


		wManager.Events.FightEvents.OnFightLoop += FightLoop;
		_isFightFixed = true;
		Log("fight fix start");
	}
	static void FixFightStop()
	{
		wManager.Events.FightEvents.OnFightLoop -= FightLoop;
		_isFightFixed = false;
		Log("fight fix stop");
	}
	static void FightLoop(WoWUnit unit, System.ComponentModel.CancelEventArgs cancelable)
	{
		if (!Quest.HasQuest(_questID))
		{
			FixFightStop();
			return;
		}
		var me = ObjectManager.Me;
		var targ = ObjectManager.Target;
		if (me.IsValid && me.IsAlive && targ.IsValid && !targ.IsAttackable)
		{
			Log("fix fight with " + targ.Name);
			cancelable.Cancel = true;
			//Interact.InteractGameObject(me.GetBaseAddress);
		}

	}
}

