public sealed class DemonSoulsHook : QuestClass
{
	Thread _thread;

	public DemonSoulsHook()
	{
		Name = "Demon Souls (Hook)";
		QuestId.Add(37653);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
	}

	public override bool IsComplete()
	{
		return _thread != null;
	}

	public override bool Pulse()
	{
		if (_thread == null)
			StartThread();
		else
			StopThread();

		return true;
	}

	void StartThread()
	{
		if (_thread != null)
			StopThread();

		_thread = new Thread(ThreadRoutine);
		_thread.Start();
	}

	void StopThread()
	{
		if (_thread == null)
			return;

		_thread.Abort();
		_thread = null;
	}

	void ThreadRoutine()
	{
		uint itemId = 122100;
		while (robotManager.Products.Products.IsStarted && Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause && Questing.Has(this))
		{
			var targ = ObjectManager.Target;
			if (targ != null && targ.IsValid && targ.IsDead && !targ.HaveBuff(178763) && !targ.HaveBuff(178974) && !targ.HaveBuff(159474))
			{
				ItemsManager.UseItem(itemId);
			}
			Thread.Sleep(1000);
		}
		StopThread();
	}

}

