QuestsSorted.NameClass:
/* Set Druid Guardian */
if (ObjectManager.Me.WowClass == wManager.Wow.Enums.WoWClass.Druid)
{
	int forcedSpec = 3; //1 balance, 2 feral, 3 guardian, 4 restoration
	robotManager.Helpful.Var.SetVar("ForcedSpecialization", new int[8] { forcedSpec, 1, 3, 3, 1, 1, 2, 1 });
	var specName = Lua.LuaDoString<string>(string.Format("return select(2, GetSpecializationInfo({0})) or 'None'", forcedSpec));
	Logging.Write("Forced specialization set to " + specName);
}

