public class MoozysReunion : QuestClass
{
	public MoozysReunion()
	{
		Name = "Moozy's Reunion";
		QuestId.Add(42590);
		Step.AddRange(new[] { 1, 1});
	}
	public override bool Pulse()
	{
		if (ObjectManager.Me.HaveBuff(214307) || ObjectManager.Me.HaveBuff(213937))
		{
			var ethel = Questing.FindUnit(107726);
			if (ethel != null && ethel.IsValid)
			{
				var p0 = ethel.Position.InFrontOf(ethel.Rotation, 4.5f);
				GoToTask.ToPosition(p0);
			}
			return true;
		}
		var p = new Vector3(4269.8, 4303.593, 738.8216);
		if (GoToTask.ToPosition(p))
		{
			Questing.DisableMount();
			GoToTask.ToPositionAndIntecractWithNpc(p, 107726, 1);
		}
		return true;
	}
}

var p = new Vector3(4057.205, 4320.374, 675.4365);
if (GoToTask.ToPositionAndIntecractWithNpc(p, 98825, 1))
{
	Questing.CancelCutscene();
}
return true;

