public class ToSeethePast : QuestClass
{
	public ToSeethePast()
	{
		Name = "To See the Past";
		QuestId.Add(40520);
		Step.AddRange(new[] { 1, 1, 1});
	}
	public override bool Pulse()
	{
		if (!Questing.ObjectiveComplete(this, 1))
		{
			var p = new Vector3(4252.739, 4470.258, 671.394);
			Questing.Buy(p, 100437, 132247);
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 3))
		{
			var p = new Vector3(4438.527, 4496.714, 707.2569);
			GoToTask.ToPositionAndIntecractWithNpc(p, 100418);
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 2))
		{
			var p = new Vector3(4329.609, 4258.665, 656.7534, "None");
			Questing.Gather(p, 245620, 8);
			return true;
		}
		return true;
	}
}

