static class HulnsWar
{
	static Vector3 NpcPosition = new Vector3(4057.205, 4320.374, 675.4365);
	static int NpcEntry = 98825;

	public static void Log(string text)
	{
		Logging.WriteDebug("[Huln's War] " + text);
	}
	public static bool TryCombat()
	{
		var target = ObjectManager.GetWoWUnitHostile().Where(u => u.IsTargetingMeOrMyPet).OrderBy(u => u.GetDistance).FirstOrDefault();
		if (target == null || !target.IsValid)
			target = ObjectManager.Pet.TargetObject;

		if (target == null || !target.IsAlive || !target.IsAttackable || !target.IsValid)
			return false;

		var p = target.Position;
		Interact.InteractGameObject(target.GetBaseAddress);
		//ObjectManager.Pet.Target = target.Guid;
		var dist = target.GetDistance;
		if (dist > 59)
		{
			GoToTask.ToPosition(p, 59);
			return true;
		}

		MovementManager.StopMove();
		//harpoon-stomp
		if (dist > 9 && SpellReady(193798))
		{
			MovementManager.Face(target);
			Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton2");
			Usefuls.WaitIsCasting();
			return true;
		}
		//gore-charge
		if (dist > 5 && SpellReady(193016))
		{
			MovementManager.Face(target);
			Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton3");
			Usefuls.WaitIsCasting();
			return true;
		}
		//wild-carve
		if (SpellReady(193237))
		{
			MovementManager.Face(target);
			Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton1");
			Usefuls.WaitIsCasting();
			return true;
		}
		return false;
	}
	public static bool TryFind(List<int> mobs, List<Vector3> hotspots)
	{
		var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mobs));
		if (mob != null && mob.IsAlive && mob.IsAttackable && mob.IsValid)
		{
			Interact.InteractGameObject(mob.GetBaseAddress);
			Thread.Sleep(Usefuls.Latency + 150);
			return true;
		}
		var p = hotspots[Others.Random(0, hotspots.Count - 1)];
		GoToTask.ToPosition(p, 3.5f, false, (c) => {
			var m = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mobs));
			if (m != null && m.IsAlive && m.IsAttackable && m.IsValid)
			{
				Interact.InteractGameObject(mob.GetBaseAddress);
				Thread.Sleep(Usefuls.Latency + 150);
				return false;
			}
			return true;
		});
		return true;
	}
	public static bool MountVehicle()
	{
		if (ObjectManager.Me.PlayerUsingVehicle)
		{
			Conditions.ForceIgnoreIsAttacked = true;
			return false;
		}
		GoToTask.ToPositionAndIntecractWithNpc(NpcPosition, NpcEntry, 1);
		return true;
	}
	public static bool PickUp()
	{
		if (Usefuls.ContinentId == (int)ContinentId.HulnHighmountain)
			return false;

		Questing.PickUp(NpcPosition, NpcEntry);
		return true;
	}
	public static bool SpellReady(uint spellID)
	{
		return Lua.LuaDoString<int>(string.Format("start, duration, enabled = GetSpellCooldown('{0}'); return start;", spellID)) == 0;
	}
}

