public class HulnsWarTheArrival : QuestClass
{
	protected List<Vector3> _hotspots = new List<Vector3>()
	{
		new Vector3(3879.25, -4900.002, 147.643),
	};
	protected List<int> _mobs = new List<int>()
	{
		97796,
		97803,
		98022,
		98020,
		109386,
	};
	public HulnsWarTheArrival()
	{
		Name = "Huln's War - The Arrival";
		QuestId.Add(39983);
		Step.AddRange(new[] { 1, 1, 1, 1 });
	}
	public override bool Pulse()
	{
		if (HulnsWar.MountVehicle())
			return true;

		if (HulnsWar.TryCombat())
			return true;

		if (HulnsWar.TryFind(_mobs, _hotspots))
			return true;

		return true;
	}

	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 2);
	}

	public override bool TurnIn()
	{
		if (HulnsWar.MountVehicle())
			return true;

		return base.TurnIn();
	}
}

