public class ThePathofHuln : QuestClass
{
	robotManager.Helpful.Timer _timer = new robotManager.Helpful.Timer(3 * 60 * 1000);
	static bool _settingsChanged = false;
	public ThePathofHuln()
	{
		Name = "The Path of Huln";
		QuestId.Add(39575);
		Step.AddRange(new[] { 1, 1 });
	}
	public override bool PickUp()
	{
		_settingsChanged = false;
		_timer.Reset();
		return base.PickUp();
	}
	Vector3 jumpStart = new Vector3(3618.73, 4439.9, 767.78, "None");
	Vector3 jumpEnd = new Vector3(3609.356, 4351.265, 663.5656, "None");
	public override bool Pulse()
	{
		if (DoObjective1())
			return true;

		if (GoToTask.ToPosition(jumpStart))
		{
			Questing.ClickMove(jumpEnd);
			Questing.Wait(10, 15);
		}
		return true;
	}
	bool DoObjective1()
	{
		if (Questing.ObjectiveComplete(this, 1))
			return false;

		var spiritwalker = Questing.FindUnit(96164);
		if (spiritwalker != null && spiritwalker.IsValid)
		{
			ChangeSettings();
			Logging.WriteDebug("[The Path of Huln] found npc, follow");
			if (spiritwalker.GetDistance > 8)
			{
				GoToTask.ToPosition(spiritwalker.Position);
				return true;
			}
			var p0 = spiritwalker.Position.InFrontOf(spiritwalker.Rotation, 7f);
			//GoToTask.ToPosition(p0);
			Questing.ClickMove(p0);
		}
		else if (_timer.IsReady)
		{
			Logging.WriteDebug("[The Path of Huln] npc didnt found and timer expired, reset quest");
			ResetSettings();
			Questing.Abandon(this);
			Questing.Wait(2);
		}
		return true;
	}
	public static void ChangeSettings()
	{
		if (_settingsChanged)
			return;

		_settingsChanged = true;
		//Conditions.ForceIgnoreIsAttacked = true;
		//Questing.DiableFlying();
		Questing.DisableMount();
	}

	public static void ResetSettings()
	{
		if (!_settingsChanged)
			return;

		_settingsChanged = false;
		//Conditions.ForceIgnoreIsAttacked = false;
		wManager.wManagerSetting.CurrentSetting.UseMount = true;
		wManager.wManagerSetting.CurrentSetting.UseFlyingMount = true;
	}
}

