public class StormforgedGrappleLauncher2 : QuestClass
{
	public StormforgedGrappleLauncher2()
	{
		Name = "Stormforged Grapple Launcher 2";
		QuestId.Add(39775);
	}
	public override bool HasQuest()
	{
		return !IsComplete();
	}
	public override bool IsComplete()
	{
		return Questing.Complete(this);
	}
	public override bool Pulse()
	{
		//horde
		var p = new Vector3(3945.718, 2888.738, 241.747);
		if (ObjectManager.Me.IsAlliance)
		{
			//p = p;
		}
		if (GoToTask.ToPosition(p))
		{
			GoToTask.ToPositionAndIntecractWithNpc(p, 107840);
			Quest.CompleteQuest();
		}
		return true;
	}
}

List<uint> objectiveBuffs = new List<uint>()
{
	0,
	219928,
	219937,
	219948,
};
List<Vector3> objectiveLoc = new List<Vector3>()
{
	Vector3.Zero,
