	new Vector3(3528.372, 2619.268, 317.8582, "None"),
	new Vector3(3522.19, 2606.716, 319.6569, "None"),
};
if (ObjectManager.Me.IsAlliance)
{
	objectiveLoc = new List<Vector3>()
	{
		Vector3.Zero,
		new Vector3(3277.933, 2544.112, 302.1056),
		new Vector3(3266.861, 2544.11, 299.6038),
