List<int> objectiveIDs = new List<int>()
{
	0,
	252074,
	252076,
	252080,
};
for (int i = 1; i < objectiveBuffs.Count; i++)
{
	var objBuff = objectiveBuffs[i];
	if (Questing.ObjectiveComplete(this, i) && ObjectManager.Me.HaveBuff(objBuff))
	{
		Logging.WriteDebug("[Havi's Test] leave completed objective #" + i + " (buff=" + objBuff + ")");
		Usefuls.EjectVehicle();
		return true;
	}
}
var cauldron = Questing.FindUnit(110604);
for (int j = 1; j < objectiveIDs.Count; j++)
{
	if (Questing.ObjectiveComplete(this, j))
		continue;

	var objBuff = objectiveBuffs[j];
	if (!ObjectManager.Me.HaveBuff(objBuff))
	{
		var objP = objectiveLoc[j];
		var objID = objectiveIDs[j];
		Questing.Gather(objP, objID);
		return true;
	}
	Questing.Vehicle.Aim(cauldron, 0.5f, Others.Random(-10, 10));
	Questing.Vehicle.UseButton(4);
	Questing.Wait(1);
	return true;
}
return true;

