public class BuilttoScale : QuestGrinderClass
{
	static List<Vector3> _hotspots = new List<Vector3>()
	{
		new Vector3(2516.44, 2421.337, 256.0741),
		new Vector3(2480.847, 2448.257, 256.4836),
		new Vector3(2428.545, 2434.163, 257.4063),
		new Vector3(2437.832, 2373.323, 256.4834),
		new Vector3(2597.026, 2561.552, 223.9652),
		new Vector3(2653.322, 2475.437, 221.3986),
		new Vector3(2559.534, 2454.604, 241.2887),
	};

	public BuilttoScale()
	{
		Name = "Built to Scale";
		QuestId.Add(38337);
		QuestId.Add(38616);
		EntryTarget.Add(91202);
		HotSpots.AddRange(_hotspots);
		Step.AddRange(new[] { 50 });
	}
	public override bool HasQuest()
	{
		return !IsCompleted();
	}
	public override bool TurnIn()
	{

		if (ObjectManager.Me.IsHorde)
			Questing.CorrectAndFindNPC(NpcTurnIn, 92561, 92566, 92567, 92568, 92569, 92573);
		else
			Questing.CorrectAndFindNPC(NpcTurnIn, 91222, 91481, 91519, 91553, 91558, 91771);

		Questing.FindNPC(NpcTurnIn, _turnInHotspots);

		Logging.Write("@ NPC turnin=" + NpcTurnIn.Id + " dist=" + ObjectManager.Me.Position.DistanceTo(NpcTurnIn.Position));
		return base.TurnIn();
	}
	List<Vector3> _turnInHotspots = new List<Vector3>()
	{
		new Vector3(3250.595, 3153.929, 527.5721, "None"),
		new Vector3(2397.605, 2304.455, 257.9244, "None"),
		new Vector3(2447.486, 2345.759, 256.1138, "None"),
	};
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 1);
	}
}

