public class ScavengingtheShallows : QuestGathererClass
{
	static List<Vector3> _hotspots = new List<Vector3>()
	{
		new Vector3(3779.802, 1789.465, 1.509373),
		new Vector3(3719.941, 1835.726, 0.9320337),
		new Vector3(3651.49, 1827.155, 0.1810672),
		new Vector3(3480.901, 1714.266, 0.277281),
		new Vector3(3600.076, 1722.715, 0.05199776),
		new Vector3(3702.514, 1728.918, 0.6329892),
	};
	static robotManager.Helpful.Timer _timer = new robotManager.Helpful.Timer(10 * 60 * 1000);
	static bool _started = false;
	public ScavengingtheShallows()
	{
		Name = "Scavenging the Shallows";
		QuestId.Add(40046);
		Step.AddRange(new[] { 1});
		HotSpots.AddRange(_hotspots);
		EntryIdObjects.Add(252239);
	}
	void Log(string text)
	{
		Logging.WriteDebug("[" + Name + "] " + text);
	}
	public override bool PickUp()
	{
		_started = false;
		Log("pickup. started=" + _started);
		return base.PickUp();
	}
	public override bool Pulse()
	{
		if (!_started)
		{
			Log("timer reset. started=" + _started + " time left=" + _timer.TimeLeft() );
			_timer.Reset();
		}
		_started = true;
		if (_timer.IsReady)
		{
			Log("abandon. started=" + _started + " timer_expire=" + _timer.IsReady);
			Questing.Abandon(this);
			return true;
		}
		return base.Pulse();
	}
}

#if VISUAL_STUDIO
using robotManager.Helpful;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading;
using System.Threading.Tasks;
using wManager.Wow.Bot.Tasks;
using wManager.Wow.Class;
using wManager.Wow.Helpers;
using wManager.Wow.ObjectManager;
using wManager.Wow.Enums;
#endif

public class TheFinalJudgment : QuestClass
{
	static Thread CombatThread = null;
	static Vector3 hotspot = new Vector3(3626.518, 791.3125, 38.72163);
	static int mobID = 93093;
	static List<int> runestonesID = new List<int>() { 97223, 97221, };
	static List<uint> runestonesBuffsID = new List<uint>() { 208815, 208816, 216204 };
	static List<uint> runeseerBuffsID = new List<uint>() { 208542, 208547, 209177, 209307, 208544 };
	static bool isCombatFixed = false;
	public TheFinalJudgment()
	{
		Name = "The Final Judgment";
		QuestId.Add(38818);
		Step.AddRange(new[] { 1 });
	}
	static void Log(string text)
	{
		Logging.WriteDebug("[The Final Judgment] " + text);
	}
	public override bool Pulse()
	{
		CombatFixStart();
		if (!Questing.Attack(mobID))
			GoToTask.ToPosition(hotspot);

		return true;
	}
	static void CombatFixStart()
	{
		if (isCombatFixed)
			return;

		CombatFixEnd();
		isCombatFixed = true;
		wManager.Events.FightEvents.OnFightLoop += OnFightLoop;
		robotManager.Events.ProductEvents.OnProductStopping += OnProductStopping;
		Log("combat fix start");
	}
	static void CombatFixEnd()
	{
		wManager.Events.FightEvents.OnFightLoop -= OnFightLoop;
		robotManager.Events.ProductEvents.OnProductStopping -= OnProductStopping;
		isCombatFixed = false;
		Log("combat fix stop");
	}
	static void OnProductStopping(string productName)
	{
		CombatFixEnd();
	}
	static void OnFightLoop(WoWUnit unit, System.ComponentModel.CancelEventArgs cancelable)
	{
		if (!Questing.Has(38818))
		{
			CombatFixEnd();
			return;
		}
		var me = ObjectManager.Me;
		if (me == null || !me.IsValid || !me.IsAlive)
		{
			return;
		}
		if (me.PlayerUsingVehicle || me.IsOnTaxi || me.HaveBuff(209393))
		{
			Log("disabled. stop move and combat");
			MovementManager.StopMove();
			cancelable.Cancel = true;
			return;
		}
		//http://www.wowhead.com/spell=208544/runic-bulwark
		if (unit.HaveBuff(runeseerBuffsID))
		{
			wManager.wManagerSetting.AddBlackList(unit.Guid, 10 * 1000, true);
			cancelable.Cancel = true;
			Log("target protected by " + SpellListManager.SpellNameInGameById(208544) +". try to attack runestone" );
			TryAttackRunestone();
			//System.Threading.Tasks.Task.Delay(5).ContinueWith(t => TryAttackRunestone());
		}
		else
		{
			if (runestonesID.Contains(unit.Entry) && unit.HealthPercent < 1 && unit.Health < 2)
			{
				wManager.wManagerSetting.AddBlackList(unit.Guid, 10 * 1000, true);
				cancelable.Cancel = true;
				Log("runestone destroyed. ignore");
			}
			var runeseer = Runeseer();
			if (runeseer != null && runeseer.IsValid && !runeseer.IsMyTarget)
			{
				wManager.wManagerSetting.AddBlackList(unit.Guid, 10 * 1000, true);
				cancelable.Cancel = true;
				Log("runeseer without protection. try to attack him");
				TryAttackRuneseer();
				//System.Threading.Tasks.Task.Delay(5).ContinueWith(t => TryAttackRuneseer());
			}
		}
		//Log("fight with " + unit.Name + " hp%=" + unit.HealthPercent + " hp=" + unit.Health );
	}
	static void ProtectedAttack(WoWUnit unit)
	{
		if (unit == null || !unit.IsValid || unit.IsDead || !unit.IsAttackable)
			return;

		Log("change fight to " + unit.Name);
		Fight.StopFight();
		Interact.InteractGameObject(unit.GetBaseAddress, false, false, true);
		Thread.Sleep(100);
		Interact.InteractGameObject(unit.GetBaseAddress);
		Fight.StartFight(unit.Guid, false, true, false, false);
	}
	static WoWUnit Runeseer()
	{
		return ObjectManager.GetObjectWoWUnit().Where(u => u.IsValid && u.Entry == mobID && u.IsAlive && u.IsAttackable && !u.HaveBuff(runeseerBuffsID)).FirstOrDefault();
	}
	static void TryAttackRuneseer()
	{
		ProtectedAttack(Runeseer());
	}
	static WoWUnit Runestone()
	{
		return ObjectManager.GetObjectWoWUnit().Where(u => u.IsValid && runestonesID.Contains(u.Entry) && u.IsAlive && u.IsAttackable && u.HaveBuff(runestonesBuffsID)).FirstOrDefault();
	}
	static void TryAttackRunestone()
	{
		ProtectedAttack(Runestone());
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 1);
	}
	public override bool TurnIn()
	{
		if (!StormheimQuests.Subzone.Hellheim.Inside)
		{
			StormheimQuests.Subzone.Hellheim.ToInside();
			return true;
		}
		return base.TurnIn();
	}
}
