QuestsSorted.NameClass:
/* Purge the Unclean */
Thread t = new Thread(() =>
{
	int questId = 40370; // http://www.wowhead.com/quest=40370/purge-the-unclean
	while (robotManager.Products.Products.IsStarted)
	{
		if (Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause)
		{
			if (!Quest.HasQuest(questId))
			{
				Logging.WriteDebug("[Purge the Unclean] no quest");
				break;
			}

			wManager.wManagerSetting.ClearBlacklistOfCurrentProductSession();
			var me = ObjectManager.Me;
			var targ = ObjectManager.Target;
			if (targ != null && targ.IsValid && targ.Entry == 99859 && targ.HealthPercent < 35)
			{

				if (targ.GetDistance > 20)
				{
					Logging.WriteDebug("[Purge the Unclean] too far. come closer");
					wManager.Wow.Bot.Tasks.GoToTask.ToPosition(targ.Position, 19.9f);
					continue;
				}
				Logging.WriteDebug("[Purge the Unclean] stop fight. purge " +targ.Name);
				Fight.StopFight();
				ItemsManager.UseItem(131760);
				Usefuls.WaitIsCasting();
			}
		}
		Thread.Sleep(1000);
	}
	Logging.WriteDebug("[Purge the Unclean] done");
});
t.Start();
Logging.WriteDebug("[Purge the Unclean] start");

