public class TheDelicateArtofTelemancy : QuestClass
{
	public TheDelicateArtofTelemancy()
	{
		Name = "The Delicate Art of Telemancy";
		QuestId.Add(40747);
		Step.AddRange(new[] { 1, 1, 1, 1 });
	}
	void Log(string text)
	{
		Logging.Write("[" + Name + "] " + text);
	}
	public override bool Pulse()
	{
		if (!Questing.ObjectiveComplete(this, 1))
		{
			Log("1) to gardens");
			SuramarQuests.Warps.WORKSHOP_GARDENS.Use();
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 3))
		{
			if (SuramarQuests.Warps.NearGardens)
			{
				Log("3) from gardens to warplab");
				SuramarQuests.Warps.GARDENS_WARPLAB.Use();
				return true;
			}
			if (SuramarQuests.Warps.NearWarpLab)
			{
				Log("3) get item");
				var p = new Vector3(1711.833, 3706.787, 261.2419);
				Questing.Gather(p, 246229);
				return true;
			}
			Log("3) empty");
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 2))
		{
			if (SuramarQuests.Warps.NearWarpLab)
			{
				Log("2) from warplab to workshop");
				SuramarQuests.Warps.WARPLAB_WORKSHOP.Use();
				return true;
			}
			if (SuramarQuests.Warps.NearWorkshop)
			{
				Log("2) from workshop to gardens");
				SuramarQuests.Warps.WORKSHOP_GARDENS.Use();
				return true;
			}
			if (SuramarQuests.Warps.NearGardens)
			{
				Log("2) from gardens to fountain");
				SuramarQuests.Warps.GARDENS_FOUNTAIN.Use();
				return true;
			}
			if (SuramarQuests.Warps.NearFountain)
			{
				Log("3) from fountain to telemlab");
				SuramarQuests.Warps.FOUNTAIN_TELEMLAB.Use();
				return true;
			}
			if (SuramarQuests.Warps.NearTelemetryLabs)
			{
				Log("3) get item");
				var p = new Vector3(2149.879, 3777.373, 462.5777);
				Questing.Gather(p, 246225);
				return true;
			}
		}
		if (SuramarQuests.Warps.NearTelemetryLabs)
		{
			Log("out) from telemlab to fountain");
			SuramarQuests.Warps.TELEMLAB_FOUNTAIN.Use();
			return true;
		}
		if (SuramarQuests.Warps.NearFountain)
		{
			Log("out) from fountain to garden");
			SuramarQuests.Warps.FOUNTAIN_GARDENS.Use();
			return true;
		}
		if (SuramarQuests.Warps.NearGardens)
		{
			Log("out) from garden");
			SuramarQuests.Warps.GARDENS_WORKSHOP.Use();
			return true;
		}
		Log("out) empty");
		return true;
	}
	public override bool IsComplete()
	{
		if (!Questing.ObjectiveComplete(this, 1))
			return false;
		if (!Questing.ObjectiveComplete(this, 2))
			return false;
		if (!Questing.ObjectiveComplete(this, 3))
			return false;
		return SuramarQuests.Warps.NearWorkshop;
	}
}

