public class NetworkSecurity : QuestGathererClass
{
	public NetworkSecurity()
	{
		Name = "Network Security";
		QuestId.Add(40748);
		EntryIdObjects.Add(246297);
		HotSpots.Add(new Vector3(2173.717, 3442.407, 221.7803));
		HotSpots.Add(new Vector3(2183.721, 3478.244, 221.7803));
		HotSpots.Add(new Vector3(2165.494, 3487.724, 221.7803));
		Step.AddRange(new[] { 10, 1, 1, 4 });
	}
	void Log(string text)
	{
		Logging.Write("[" + Name + "] " + text);
	}
	public override bool Pulse()
	{
		if (!Questing.ObjectiveComplete(this, 1))
		{
			Log("1");
			SuramarQuests.Warps.WORKSHOP_TESTCHAMBER.Use();
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 2))
		{
			Log("2");
			var p = new Vector3(2192.561, 3346.08, 249.1024);
			Questing.Gather(p, 246328);
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 3))
		{
			Log("3");
			return base.Pulse();
		}
		if (!Questing.ObjectiveComplete(this, 4))
		{
			Log("4");
			if (!Questing.Attack(101116))
			{
				var p = new Vector3(2328.63, 3546.813, 564.2925);
				GoToTask.ToPosition(p);
			}
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 5))
		{
			Log("5");
			var p = new Vector3(2326.536, 3546.114, 564.374);
			GoToTask.ToPositionAndIntecractWithNpc(p, 101782, 1);
			return true;
		}
		Log("%6");
		return true;
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 5);
	}
}

