	public PowerGrid()
	{
		Name = "Power Grid";
		QuestId.Add(41028);
		Step.AddRange(new[] { 1, 1, 1, 1 });
		_switches.ForEach(ps => ps.isComplete = false);
	}
	public override bool Pulse()
	{
		if (!Questing.ObjectiveComplete(this, 1))
		{
			if (UsePowerSwitch(0))
			{
				if (UsePowerSwitch(1))
				{
				}
			}
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 2))
		{
			if (UsePowerSwitch(2))
			{
				if (UsePowerSwitch(3))
				{
					if (UsePowerSwitch(4))
					{
						if (UsePowerSwitch(5))
						{
						}
					}
				}
			}
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 3))
		{
			if (UsePowerSwitch(6))
			{
				if (UsePowerSwitch(7))
				{
					if (UsePowerSwitch(8))
					{
						if (UsePowerSwitch(9))
						{
						}
					}
				}
			}
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 4))
		{
			if (UsePowerSwitch(10))
			{
				if (UsePowerSwitch(11))
				{
					if (UsePowerSwitch(12))
					{
						if (UsePowerSwitch(13))
						{
						}
					}
				}
			}
			return true;
		}
		return true;
	}
	bool UsePowerSwitch(int i)
	{
		PowerSwitch ps = _switches[i];
		if (ps.isComplete)
			return true;

		Logging.WriteDebug("use power #" + i);
		if (GoToTask.ToPositionAndIntecractWithGameObject(ps.position, ps.Entry))
		{
			ps.isComplete = true;
			return true;
		}
		return false;
	}

}
