/* Lay Waste, Lay Mines: Trap */
Thread t = new Thread(() =>
{
 while (robotManager.Products.Products.IsStarted)
 {
  if (Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause)
  {
   if (!Questing.Has(40965))
    break;

   if (ObjectManager.Me.InCombat && ItemsManager.HasItemById(133882) && Questing.ItemCooldown(133882) < 1)
   {
    ItemsManager.UseItem(133882);
    Usefuls.WaitIsCasting();
   }
  }
  Thread.Sleep(500);
 }
});
t.Start();

