		new Vector3(2379.893, 4696.706, 153.2943),
		new Vector3(2317.932, 4776.167, 147.2014),
	};
	public LeylineFeedLeyStationMoonfall()
	{
		Name = "Leyline Feed: Ley Station Moonfall";
		QuestId.Clear();
		QuestId.Add(43590);
	}
