public class NotTheirLastStand : QuestInteractWithClass
{
	static robotManager.Helpful.Timer _timer = new robotManager.Helpful.Timer(5 * 60 * 1000);

	public NotTheirLastStand()
	{
		Name = "Not Their Last Stand";
		QuestId.Add(40949);
		Step.AddRange(new[] { 5 });
		HotSpots.Add(new Vector3(2791.055, 4909.261, 200.2922));
		HotSpots.Add(new Vector3(2828.634, 4887.414, 201.3857));
		HotSpots.Add(new Vector3(2828.634, 4887.414, 201.3857));
		HotSpots.Add(new Vector3(2809.115, 4871.171, 202.363));
		HotSpots.Add(new Vector3(2796.161, 4973.941, 199.414));
		HotSpots.Add(new Vector3(2761.962, 4979.903, 199.9314));
		HotSpots.Add(new Vector3(2779.171, 5001.753, 200.2298));
		HotSpots.Add(new Vector3(2746.847, 4948.782, 199.8032));
		HotSpots.Add(new Vector3(2726.377, 4937.93, 201.6899));
		EntryIdTarget.Add(101780);
	}
	static void Log(string text)
	{
		Logging.WriteDebug("[Not Their Last Stand] " + text);
	}
	public override bool TurnIn()
	{
		if (_timer.IsReady)
		{
			Log("timer ready reset");
			wManager.wManagerSetting.ClearBlacklistOfCurrentProductSession();
			_timer.Reset();
		}

		var npcTurnIn = NpcTurnIn;
		var npc = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(npcTurnIn.Id));
		if (npc == null || !npc.IsValid)
		{
			Log("no turn in npc. goto hotspot");
			GoToTask.ToPosition(npcTurnIn.Position);
			return false;
		}

		GoToTask.ToPositionAndIntecractWithNpc(npcTurnIn.Position, npcTurnIn.Id);
		Thread.Sleep(Others.Random(2000, 3000));
		if (!Questing.IsVisible("QuestFrame") && ObjectManager.Target != null && ObjectManager.Target.IsValid && ObjectManager.Target.Entry == npcTurnIn.Id)
		{
			Log("blacklist npc. no quest turn in");
			wManager.wManagerSetting.AddBlackList(ObjectManager.Target.Guid, 2 * 60 * 1000); //5 min blacklist
			return false;
		}
		Log("all fine. default turn in");
		return base.TurnIn();
	}
}

