public class WaitingforRevenge : QuestInteractWithClass
{
	public WaitingforRevenge()
	{
		Name = "Waiting for Revenge";
		QuestId.Add(41110);
		QuestId.Add(41109);
		Step.AddRange(new[] { 1, 1 });
		EntryIdTarget.Add(111604);
		HotSpots.Add(new Vector3(2830.336, 4701.386, 285.4977));
	}
	public override bool PickUp()
	{
		/* Recovering Stolen Power */
		Questing.CompleteAutoCompletableQuest(40968);
		Quest.AcceptQuest();
		DismountHyppo();
		return base.PickUp();
	}
	public override bool Pulse()
	{
		Questing.CompleteAutoCompletableQuest(this);
		DismountHyppo();
		Questing.Wait(5, 7);
		return base.Pulse();
	}
	public override bool IsComplete()
	{
		return Questing.Complete(this) && !ObjectManager.Me.PlayerUsingVehicle;
	}
	public override bool TurnIn()
	{
		return Pulse();
	}
	void DismountHyppo()
	{
		Lua.LuaDoString("CastPetAction(2);");
	}
}

