if (crystal != null && crystal.IsValid)
{
	if (PickUpQuestOnItemID >= 10)
	{
		PickUpQuestOnItemID = 0; //use as temp var
		Logging.Write("black list crystal");
		wManager.wManagerSetting.AddBlackList(crystal.Guid, 5 * 60 * 1000); //5 min blacklist
	}
	else
	{
		PickUpQuestOnItemID += 1; //use as temp var
		Logging.Write("use crystal " + PickUpQuestOnItemID);
		GoToTask.ToPositionAndIntecractWithGameObject(crystal.Position, crystal.Entry);
	}
	return true;
}
Logging.Write("to start position");
GoToTask.ToPosition(p);
return true;

